import React from "react";
import styled, { css } from "styled-components";
import { useSpeechRecognition } from "react-speech-kit";
import { Button } from "./Mixin";

const SearchMicWrap = styled(Button)`
  display: flex;
  justify-content: center;
  align-items: center;
  position: relative;
  top: -138px;
  left: 404px;
  &:hover {
    cursor: pointer;
  }
  &:active {
    background-color: var(--color-light-green);
  }
  & > div {
    display: ${(props) => props.display};
    position: absolute;
    background-color: var(--color-focus);
    width: 100px;
    line-height: var(--font-size-large);
    top: -32px;
    left: -36px;
  }
  & > div:after {
    content: "";
    position: absolute;
    display: block;
    border-style: solid;
    border-width: 8px 8px 0;
    border-color: var(--color-focus) transparent;
    width: 0;
    bottom: -6px;
    left: 40px;
  }
`;

const SearchMic = ({ setKeyword = () => {} }) => {
  const { listen, listening, stop } = useSpeechRecognition({
    onResult: (result) => {
      setKeyword(result);
    },
  });

  return (
    <SearchMicWrap
      className="globalIconBtn"
      onMouseDown={listen}
      onMouseUp={stop}
    >
      <i className="fas fa-microphone"></i>
      <div display={listening ? "block" : "none"}>
        {listening ? "음성 인식 중" : null}
      </div>
    </SearchMicWrap>
  );
};

export default SearchMic;
