import { useState } from "react";

const UseToggleBGColor = () => {
  const [clicked, setClicked] = useState("");
  const setFocusColor = () => setClicked("var(--color-focus)");
  const setNormalColor = () => setClicked("");
  const toggleBGColor = () => {
    clicked === "" ? setFocusColor() : setNormalColor();
  };
  return { setFocusColor, setNormalColor, toggleBGColor };
};

export default UseToggleBGColor;
