import React, { useState } from "react";
import CustomGoogleMap from "./CustomGoogleMap";
import Geocode from "react-geocode";

const GoogleMapSettings = () => {
  // 리버스 Geocode (주소로 좌표 구하기)
  // 상위 컴포넌트에서 받아온 주소 세 개
  const [addrs, setAddrs] = useState([
    "비트캠프 신촌",
    "서울특별시 마포구 신수동 번지 1층 89-10",
    "피자헛 신수점",
  ]);
  // 좌표를 담을 배열(기본 빈 배열)
  const [places, setPlaces] = useState([]);
  // 기본 설정(key, 언어, 장소)
  Geocode.setApiKey(process.env.REACT_APP_MY_KEY);
  Geocode.setLanguage("ko");
  Geocode.setRegion("ko");
  // 주소로 좌표 구하기
  const getLatLng = (address) => {
    Geocode.fromAddress(addrs[0]).then(
      (response) => {
        const { lat, lng } = response.results[0].geometry.location;
        console.log(lat, lng);
        setPlaces((prevPlaces) => [...prevPlaces, { lat: lat, lng: lng }]);
      },
      (error) => {
        console.log(error);
      }
    );
  };
  getLatLng(addrs[0]);
  getLatLng(addrs[1]);
  getLatLng(addrs[2]);

  // 장소1, 2, 3의 위도, 경도값('순서' 필드 참조)
  // const places = [
  //   { lat: 33.499648, lng: 126.531275 },
  //   { lat: 33.2553, lng: 126.560114 },
  //   { lat: 37.34859, lng: 126.958301 },
  // ];
  return (
    <div>
      <CustomGoogleMap
        startPoint={{ lat: places[0].lat, lng: places[0].lng }}
        endPoint={{ lat: places[1].lat, lng: places[1].lng }}
      />
    </div>
  );
};

export default GoogleMapSettings;
