// 달력 스타일링 마무리

import React, { useEffect, useState } from "react";
import styled from "styled-components";
import Header from "../components/header/Header";
import Footer from "../components/footer/Footer";
import ImageSlider from "../components/body/imageSlider/ImageSlider";
import Dots from "../components/body/imageSlider/Dots";
import {
  PointLetter,
  BodyLayout,
  StyledButton,
  MenuBox,
  SubMenuTitle,
  MyMenuItemBox,
} from "../components/body/mixin/Mixin";
import CustomCalerdar from "../components/body/calendar/CustomCalerdar";
import { Link } from "react-router-dom";
import { read_cookie } from "sfcookies";
import axios from "axios";

const MenuSection = styled.section`
  width: 60vw;
  height: 140vh;
  margin: var(--margin-default);
  display: flex;
  justify-content: center;
  flex-wrap: wrap;
`;

const Container = styled.div`
  width: 40%;
  min-width: 360px;
  height: 40%;
  margin: var(--margin-default);
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  background-color: var(--color-bg);
  border-radius: 8px;
`;

const HomeMenuBox = styled(MenuBox)`
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const HomeSubMenuTitle = styled(SubMenuTitle)`
  width: 100%;
  background-color: var(--color-light-green);
  text-align: center;
`;

const SubHeading = styled.div`
  margin-top: calc(var(--margin-default)) 0;
  font-size: var(--font-size-title-normal);
`;

const Article = styled.article`
  width: 100%;
  height: calc(100% - var(--margin-default) - var(--font-size-title-normal));
  padding: var(--padding-default);
  font-size: var(--font-size-title-small);
  display: flex;
  flex-direction: column;
  align-items: flex-start;
`;

const ArticleItem = styled.div`
  margin: calc(var(--margin-default) / 2) calc(var(--margin-default) / 4);
`;

const StyledButtonWidthMargin = styled(StyledButton)`
  margin-top: ${(props) => props.fromTop};
  margin-bottom: 20px;
`;

const MemberHome = () => {
  const [isLoaded, setIsLoaded] = useState(false);
  const [userData, setUserData] = useState(null);
  const [selectedDate, setSelectedDate] = useState(null);
  const [idx, setIdx] = useState("first");
  // console.log("idx in member home", idx);
  // 날짜 선택하고 course로 넘어가게 하기
  // console.log("멤버홈", selectedDate);

  useEffect(() => {
    // 마이페이지에서 수정하러 왔을 때만 axios에서 유저 정보 받아오기
    if (read_cookie("userId").length > 0) {
      // 쿠키 읽어서 유저 아이디가 있다면 서버에서 정보 받아오기
      const userId = read_cookie("userId");
      const body = {
        userId: userId,
      };
      axios
        .post("/wherewego/getUserData", body)
        .then((response) => {
          // console.log(response.data);
          setUserData(response.data);
          setIsLoaded(true);
        })
        .catch((error) => console.log(error));
    }
  }, []);
  // console.log(userData);

  const getOnlyYMD = (dateString) => {
    const year = dateString.substring(0, 4);
    const month = dateString.substring(5, 7);
    const date = dateString.substring(8, 10);
    return `${year}-${month}-${date}`;
  };

  const calculateDDays = (calculatedDate) => {
    if (isLoaded) {
      if (userData.startdate !== null) {
        console.log("기념일", userData.startdate);

        const ymdString = getOnlyYMD(userData.startdate);
        console.log(ymdString);
        // const year = userData.regdate.substring(0, 4);
        // const month = userData.regdate.substring(5, 7);
        // const date = userData.regdate.substring(8, 10);
        // const dDay = new Date(year, month - 1, date);
        // console.log(dDay);
      }
    }
  };
  calculateDDays();
  return (
    <>
      <Header />
      <BodyLayout padding="0">
        <ImageSlider idx={idx} setIdx={setIdx} />
        <Dots idx={idx} setIdx={setIdx} />
        <MenuSection>
          <Container>
            {/* <SubHeading> */}
            {/* <PointLetter>D</PointLetter>+ 36,500 */}
            {/* </SubHeading> */}
            <HomeMenuBox>
              {isLoaded && userData?.startdate === null ? (
                <>
                  <HomeSubMenuTitle>기념일을 설정해 주세요!</HomeSubMenuTitle>
                  <Link to={`/individualform`}>
                    <StyledButtonWidthMargin fromTop="400px">
                      기념일 설정하러 가기
                    </StyledButtonWidthMargin>
                  </Link>
                </>
              ) : (
                <>
                  <HomeSubMenuTitle>
                    {userData?.name}님의 기념일({userData?.startdate})로부터
                    <PointLetter> D</PointLetter>
                    +365
                  </HomeSubMenuTitle>
                  <MyMenuItemBox>
                    36,600 기념일 <PointLetter>D-100</PointLetter> (2031/12/10)
                  </MyMenuItemBox>
                  <MyMenuItemBox>
                    36,700 기념일 <PointLetter>D-200</PointLetter> (2032/03/20)
                  </MyMenuItemBox>
                  <MyMenuItemBox>
                    36,800 기념일 <PointLetter>D-300</PointLetter> (2032/06/30)
                  </MyMenuItemBox>
                  <MyMenuItemBox>
                    101주년 <PointLetter>D-365</PointLetter> (2032/09/05)
                  </MyMenuItemBox>
                  <MyMenuItemBox>
                    36,900 기념일 <PointLetter>D-400</PointLetter> (2032/10/10)
                  </MyMenuItemBox>
                  <MyMenuItemBox>
                    37,000 기념일 <PointLetter>D-500</PointLetter> (2032/01/10)
                  </MyMenuItemBox>
                </>
              )}
            </HomeMenuBox>
          </Container>
          )
          <Container>
            <HomeMenuBox>
              <HomeSubMenuTitle>플랜을 세워 보세요!</HomeSubMenuTitle>
              {/* 예약시에는(예약일) minDate 오늘, 가입시에는(시작일) maxDate 오늘 */}

              <CustomCalerdar setSelectedDate={setSelectedDate} />
              {selectedDate !== null ? (
                <Link to={`/planning/${selectedDate}`}>
                  <StyledButtonWidthMargin>
                    {`${selectedDate.getFullYear()}/${
                      selectedDate.getMonth() + 1
                    }/${selectedDate.getDate()} 데이트 `}
                    예약하러 가기
                  </StyledButtonWidthMargin>
                </Link>
              ) : (
                <Link to={`/planning/`}>
                  <StyledButtonWidthMargin>
                    예약하러 가기
                  </StyledButtonWidthMargin>
                </Link>
              )}
            </HomeMenuBox>
          </Container>
          <Container>
            <SubHeading>예약</SubHeading>
            <Article>
              <ArticleItem>
                잠실역 데이트(2031/12/10) 14:00~18:00(코스 요약 추가)
              </ArticleItem>
            </Article>
          </Container>
          <Container>
            <SubHeading>이력</SubHeading>
            <Article></Article>
          </Container>
        </MenuSection>
      </BodyLayout>
      <Footer />
    </>
  );
};

export default MemberHome;
