import React from "react";
import { Link } from "react-router-dom";
import styled from "styled-components";
import { MyMenuItemBox, StyledButton } from "../mixin/Mixin";

const TitleP = styled.p`
  width: 148px;
  font-weight: bold;
  display: -webkit-box;
  -webkit-box-orient: vertical;
  -webkit-line-clamp: 1;
  overflow: hidden;
  white-space: pre-wrap;
  text-overflow: ellipsis;
`;
const ContentP = styled.p`
  width: 220px;
  display: -webkit-box;
  -webkit-box-orient: vertical;
  -webkit-line-clamp: 1;
  overflow: hidden;
  white-space: pre-wrap;
  text-overflow: ellipsis;
`;

const MyReviews = (userReview = null) => {
  const userReviewData = userReview?.userReview;
  console.log("마이리뷰", userReview);
  // console.log("옵셔널 체이닝 후 리뷰", userReviewData);
  return (
    <div>
      {userReviewData?.length > 0 ? (
        userReviewData.map((data) => {
          return (
            <MyMenuItemBox>
              <div>
                <i className="fas fa-edit"></i>
                <TitleP>{data.name}</TitleP>
              </div>
              <ContentP>{data.info}</ContentP>
            </MyMenuItemBox>
          );
        })
      ) : (
        <MyMenuItemBox>
          이용 내역이 없습니다.
          <Link to={`/userreview`}>
            <StyledButton>예약하러 가기</StyledButton>
          </Link>
        </MyMenuItemBox>
      )}
    </div>
  );
};

export default MyReviews;
