import React, { useState } from "react";
import styled from "styled-components";
import { Link } from "react-router-dom";
import Header from "../components/header/Header";
import Footer from "../components/footer/Footer";
import { Button, BodyLayout } from "../components/body/mixin/Mixin";
import PageSlider from "../components/body/mixin/PageSlider";
import GoogleMapSettings from "../components/body/map/GoogleMapSettings";

const Slider = styled.div`
  width: 100vw;
  overflow: hidden;
  display: flex;
  justify-content: flex-start;
`;

const Container = styled.div`
  width: 100vw;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const ButtonContainer = styled.div`
  width: 200px;
  display: flex;
  justify-content: center;
`;

const DetermineButtons = styled.div`
  width: 400px;
  display: flex;
  justify-content: center;
`;

// 코스 정보 받아와서 > 장소1, 장소2, 장소3 주소를 구글맵으로 보내서 찍기
const Result = () => {
  const [idx, setIdx] = useState(0);
  const [address1, setAddress1] = useState("비트캠프 신촌");
  const [address2, setAddress2] = useState("비트캠프 신촌");
  const [address3, setAddress3] = useState("피자헛 신수점");
  const onClickNext = () => {
    setIdx((prevIdx) => prevIdx + 1);
  };
  const onClickPrev = () => {
    setIdx((prevIdx) => prevIdx - 1);
  };

  return (
    <>
      <Header />
      <BodyLayout>
        {/* 요약 */}
        <div>
          <div>잠실역 데이트</div>
          <div>10:00~14:00</div>
          <div>잠실역</div>
        </div>
        <Slider>
          <PageSlider idx={idx} rate={-25}>
            <Container>
              <div>코스 요약(장소 1, 2, 3)</div>
              <GoogleMapSettings
                address1={address1}
                address2={address2}
                address3={address3}
              />

              <div>날씨 소개</div>
              <div>추천 코디</div>
            </Container>
            <Container>
              <div>장소1 소개</div>
            </Container>
            <Container>
              <div>장소2 소개</div>
            </Container>
            <Container>
              <div>장소3 소개</div>
            </Container>
          </PageSlider>
        </Slider>
        {idx === 0 ? (
          <ButtonContainer>
            <Button onClick={onClickNext}>다음</Button>
          </ButtonContainer>
        ) : idx === 1 ? (
          <ButtonContainer>
            <Button onClick={onClickPrev}>이전</Button>
            <Button onClick={onClickNext}>다음</Button>
          </ButtonContainer>
        ) : idx === 2 ? (
          <ButtonContainer>
            <Button onClick={onClickPrev}>이전</Button>
            <Button onClick={onClickNext}>다음</Button>
          </ButtonContainer>
        ) : idx === 3 ? (
          <ButtonContainer>
            <Button onClick={onClickPrev}>이전</Button>
          </ButtonContainer>
        ) : null}

        <DetermineButtons>
          <Link to={"/planning"}>
            <Button>다시 선택하기</Button>
          </Link>
          <Button>휴대폰 알람 설정하기</Button>
        </DetermineButtons>
      </BodyLayout>
      <Footer />
    </>
  );
};

export default Result;
