import { useState } from "react";

const UseToggle = () => {
  const [visiblity, setVisiblity] = useState(false);

  const setOpen = () => {
    setFullHeight("60px");
    setArrowRotation("0.5turn");
  };
  const setClose = () => {
    setFullHeight("0");
    setArrowRotation("0");
  };
  const toggleState = () => {
    visiblity ? setClose() : setOpen();
  };
  return { setOpen, setClose, toggleState };
};

export default UseToggle;
