import React, { useEffect } from "react";
import styled from "styled-components";

const TMapContainer = styled.div`
  width: 800px;
  height: 400px;
`;

const CustomTMap = () => {
  useEffect(() => {
    // 지도 생성
    let script = document.createElement("script");
    script.innerHTML = `
    function initTmap() {
        let map = new Tmapv2.Map("TMapApp", {
            center: new Tmapv2.LatLng(37.566481622437934,126.98502302169841),
            width: "800px",
            height: "400px",
            zoom:15
        });

        // 시작 심볼
        let marker_s = new Tmapv2.Marker({
          position: new Tmapv2.LatLng(37.5668986, 126.97871544),
          icon: "http://tmapapi.sktelecom.com/upload/tmap/marker/pin_r_m_s.png",
          iconSize: new Tmapv2.Size(24, 38),
          map: map,
        });

        // 도착
        let marker_e = new Tmapv2.Marker({
          position: new Tmapv2.LatLng(37.57081522, 127.00160213),
          icon: "http://tmapapi.sktelecom.com/upload/tmap/marker/pin_r_m_e.png",
          iconSize: new Tmapv2.Size(24, 38),
          map: map,
        });

        
        // 경로 탐색 API 호출
        $.ajax({
          method: "POST",
          url: "https://apis.openapi.sk.com/tmap/routes/pedestrian?version=1&format=json&callback=result",
          async: false,
          data: {
            appKey: "발급AppKey",
            startX: "126.97871544",
            startY: "37.56689860",
            endX: "127.00160213",
            endY: "37.57081522",
            reqCoordType: "WGS84GEO",
            resCoordType: "EPSG3857",
            startName: "출발지",
            endName: "도착지",
          },
          success: function (response) {
            var resultData = response.features;
    
            //결과 출력
            var tDistance =
              "총 거리 : " +
              (resultData[0].properties.totalDistance / 1000).toFixed(1) +
              "km,";
            var tTime =
              " 총 시간 : " +
              (resultData[0].properties.totalTime / 60).toFixed(0) +
              "분";
    
            $("#result").text(tDistance + tTime);
    
            //기존 그려진 라인 & 마커가 있다면 초기화
            if (resultdrawArr.length > 0) {
              for (var i in resultdrawArr) {
                resultdrawArr[i].setMap(null);
              }
              resultdrawArr = [];
            }
    
            drawInfoArr = [];
    
            for (var i in resultData) {
              //for문 [S]
              var geometry = resultData[i].geometry;
              var properties = resultData[i].properties;
              var polyline_;
    
              if (geometry.type == "LineString") {
                for (var j in geometry.coordinates) {
                  // 경로들의 결과값(구간)들을 포인트 객체로 변환
                  var latlng = new Tmapv2.Point(
                    geometry.coordinates[j][0],
                    geometry.coordinates[j][1]
                  );
                  // 포인트 객체를 받아 좌표값으로 변환
                  var convertPoint =
                    new Tmapv2.Projection.convertEPSG3857ToWGS84GEO(latlng);
                  // 포인트객체의 정보로 좌표값 변환 객체로 저장
                  var convertChange = new Tmapv2.LatLng(
                    convertPoint._lat,
                    convertPoint._lng
                  );
                  // 배열에 담기
                  drawInfoArr.push(convertChange);
                }
              } else {
                var markerImg = "";
                var pType = "";
                var size;
    
                if (properties.pointType == "S") {
                  //출발지 마커
                  markerImg =
                    "http://tmapapi.sktelecom.com/upload/tmap/marker/pin_r_m_s.png";
                  pType = "S";
                  size = new Tmapv2.Size(24, 38);
                } else if (properties.pointType == "E") {
                  //도착지 마커
                  markerImg =
                    "http://tmapapi.sktelecom.com/upload/tmap/marker/pin_r_m_e.png";
                  pType = "E";
                  size = new Tmapv2.Size(24, 38);
                } else {
                  //각 포인트 마커
                  markerImg = "http://topopen.tmap.co.kr/imgs/point.png";
                  pType = "P";
                  size = new Tmapv2.Size(8, 8);
                }
    
                // 경로들의 결과값들을 포인트 객체로 변환
                var latlon = new Tmapv2.Point(
                  geometry.coordinates[0],
                  geometry.coordinates[1]
                );
    
                // 포인트 객체를 받아 좌표값으로 다시 변환
                var convertPoint = new Tmapv2.Projection.convertEPSG3857ToWGS84GEO(
                  latlon
                );
    
                var routeInfoObj = {
                  markerImage: markerImg,
                  lng: convertPoint._lng,
                  lat: convertPoint._lat,
                  pointType: pType,
                };
    
                // Marker 추가
                marker_p = new Tmapv2.Marker({
                  position: new Tmapv2.LatLng(routeInfoObj.lat, routeInfoObj.lng),
                  icon: routeInfoObj.markerImage,
                  iconSize: size,
                  map: map,
                });
              }
            } //for문 [E]
            drawLine(drawInfoArr);
          },
          error: function (request, status, error) {
            console.log(
              "code:" +
                request.status +
                "\n" +
                "message:" +
                request.responseText +
                "\n" +
                "error:" +
                error
            );
          },
        });
    }
    initTmap();
    `;
    script.type = "text/javascript";
    script.async = "async";
    document.head.appendChild(script);
  }, []);

  return <TMapContainer id="TMapApp"></TMapContainer>;
};

export default CustomTMap;
