import React from "react";
import styled from "styled-components";
import {
  BodyLayout,
  Input,
  MenuTitle,
  ReviewIcon,
  ReviewImageBox,
  ReviewItem,
  ReviewProfileBox,
  ReviewTextBox,
} from "../components/body/mixin/Mixin";
import Stars from "../components/body/mixin/Stars";
import Footer from "../components/footer/Footer";
import Header from "../components/header/Header";

const ReviewItemWidthMargin = styled(ReviewItem)`
  margin-top: var(--margin-default);
`;

const ReviewInput = styled(Input)`
  width: 100%;
`;

const UserInfo = styled.div`
  width: calc(100% - 60px);
`;

const NameAndStar = styled.div`
  display: flex;
`;

const UserName = styled.div`
  width: 48%;
  min-width: 120px;
  font-size: var(--font-size-large);
`;

const UserHistory = styled.div`
  font-size: var(--font-size-normal);
`;

const UserReview = () => {
  return (
    <>
      <Header />
      <BodyLayout>
        <MenuTitle>리뷰 작성</MenuTitle>
        <ReviewItemWidthMargin as="li">
          <Input type="text" />
          <ReviewImageBox>
            <img
              // src={`${process.env.PUBLIC_URL}/images/reviews/${review.r_num}.jpg`}
              alt="cafe_review"
            />
          </ReviewImageBox>
          <ReviewTextBox>{/* <p>{review.info}</p> */}</ReviewTextBox>
          <ReviewProfileBox>
            <ReviewIcon>
              <img
                // src={`${process.env.PUBLIC_URL}/images/users/${review.userid}.jpg`}
                alt="user_icon"
              />
            </ReviewIcon>
            <UserInfo>
              <NameAndStar>
                <UserName>ㅎㅎ</UserName>
                <Stars score={10} />
              </NameAndStar>
              <UserHistory>
                {/* {"20" + review.resdate.split("/")[0]}. */}
                {/* {review.resdate.split("/")[1]}.{review.resdate.split("/")[2]}{" "} */}
                방문
              </UserHistory>
            </UserInfo>
          </ReviewProfileBox>
        </ReviewItemWidthMargin>
      </BodyLayout>
      <Footer />
    </>
  );
};

export default UserReview;
