import { useState } from "react";

const UseToggle = () => {
  const [visiblity, setVisiblity] = useState(false);
  const setVisible = () => setVisiblity(true);
  const setInvisible = () => setVisiblity(false);
  const toggleVisiblity = () => {
    visiblity ? setVisible() : setInvisible();
  };

  return { setVisible, setInvisible, toggleVisiblity };
};

export default UseToggle;
