import React, { useState } from "react";
import styled from "styled-components";
import axios from "axios";
import Form from "../mixin/Form";
import {
  Label,
  SubmitButton,
  ItemContainer,
  FormInput,
  Hyphen,
  ErrorMsg,
} from "../registrationForm/FormMixin";
import { Button } from "../mixin/Mixin";

const Container = styled.div`
  overflow: hidden;
  transition-delay: ${(props) => {
    return props.isVisible ? "0" : "0.5s";
  }};
  transition-duration: 0.5s;
  height: ${(props) => {
    return props.isVisible ? "0" : "72vh";
  }};
`;

const FlexForm = styled(Form)`
  align-items: flex-start;
`;

// 제출 박스 컨테이너
const SubmitSection = styled.section`
  width: 100%;
  display: flex;
  justify-content: flex-end;
`;

const CompanyForm = ({ isIndividual = true }) => {
  const [test, setTest] = useState("");
  const [userId, setUserId] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    let body = {
      testData: test,
    };
    axios
      // .post("/wherewego/user/getUserData", body)
      .post("/wherewego/registertest", body)
      .then((response) => console.log(response));
    // window.location.href = "http://localhost:3000/";
  };

  return (
    <Container isVisible={isIndividual}>
      <FlexForm id="company-form" onSubmit={handleSubmit}>
        <ItemContainer>
          <Label htmlFor="userId">아이디</Label>
          <FormInput
            type="text"
            name="userId"
            id="userId"
            required
            minLenght="6"
            maxLength="14"
            pattern="[A-Za-z]{1}\w{5,14}"
            placeholder=" "
            onKeyDown={(e) => setUserId(e.target.value)}
          />
          <Button type="button">중복 확인</Button>
          <ErrorMsg>
            아이디는 6~14자의 영문 대소문자, 숫자로 이루어져야 하며, 첫 글자는
            영문자만 입력할 수 있습니다.
          </ErrorMsg>
        </ItemContainer>
        <ItemContainer>
          <Label htmlFor="pwd">비밀번호</Label>
          <FormInput
            type="password"
            name="pwd"
            id="pwd"
            required
            minLength="8"
            maxLength="16"
            pattern="^(?=.*?[A-Z])(?=.*?[a-z])(?=.*?[0-9])(?=.*?[#?!@$%^&*-]).{8,16}$"
            placeholder=" "
            onKeyDown={(e) => setPwd(e.target.value)}
          />
          <ErrorMsg>
            비밀번호는 8자 이상이어야 하며, 숫자, 영문 대소문자, 특수문자를 모두
            포함해야 합니다.
          </ErrorMsg>
        </ItemContainer>
        <ItemContainer>
          <Label htmlFor="userPwd2">비밀번호 확인</Label>
          <FormInput
            type="password"
            name="userPwd2"
            id="userPwd2"
            required
            maxLength="16"
            placeholder=" "
            onKeyUp={checkPwd}
            className="check"
            isSame={isSame}
            autoComplete="new-password"
          />
          <ErrorMsg>비밀번호가 일치하지 않습니다.</ErrorMsg>
        </ItemContainer>
        <ItemContainer>
          <Label>상호명</Label>
          <FormInput type="text" name="" />
        </ItemContainer>
        <ItemContainer>
          <Label>사업자등록번호</Label>
          <FormInput type="text" name="" placeholder="2자리" />
          <Hyphen> - </Hyphen>
          <FormInput type="text" name="" placeholder="3자리" />
          <Hyphen> - </Hyphen>
          <FormInput type="text" name="" placeholder="5자리" />
        </ItemContainer>
        <ItemContainer>
          <Label>대표 연락처</Label>
          <FormInput type="text" name="" />
        </ItemContainer>
        <ItemContainer>
          <Label>우편번호</Label>
          <FormInput type="text" name="" />
          <Button>우편번호 검색</Button>
        </ItemContainer>
        <ItemContainer>
          <Label>주소</Label>
          <FormInput type="text" name="" />
        </ItemContainer>
        <ItemContainer>
          <Label>상세주소</Label>
          <FormInput type="text" name="" />
        </ItemContainer>

        <SubmitSection>
          <SubmitButton as="FormInput" type="reset" value="초기화" />
          <SubmitButton as="FormInput" type="submit" value="회원가입" />
        </SubmitSection>
      </FlexForm>
    </Container>
  );
};

export default CompanyForm;
