  Hyphen,
  Checkbox,
  CheckboxLabel,
  SubmitButton,
  ItemContainer,
  IDContainer,
  PWDContainer,
  PWDCheckContainer,
  NameContainer,
  PersonalNumContainer,
  PhoneNumContainer,
} from "../registrationForm/FormMixin";

const Container = styled.div`
  overflow: hidden;
  transition-delay: ${(props) => {
    return props.isVisible ? "0.5s" : "0";
  }};
  // auto에는 트랜지션 속도가 적용되지 않는 듯
  transition-duration: 0.5s;
  height: ${(props) => {
    return props.isVisible ? "120vh" : "0";
  }};
`;

const TopSection = styled.section`
  width: 100%;
  display: flex;
`;

const LeftSection = styled.section`
  width: 72%;
`;
const RightSection = styled.section`
  width: 28%;
  padding: var(--padding-default);
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
  align-items: center;
`;

const BottomSection = styled.section`
  width: 100%;
`;

// 옵션 컨테이너
const OptionsContainer = styled.div`
  max-width: 40vw;
  height: auto;
  margin-top: var(--margin-default);
  border: 2px solid var(--color-brown);
  border-radius: 4px;
  display: flex;
  flex-wrap: wrap;
  padding: var(--padding-default);
`;

// 사진 입력 컨테이너
const UserImgContainer = styled.div`
  width: 240px;
  height: 240px;
  border-radius: 50%;
  margin-bottom: var(--margin-line-space);
  display: flex;
  justify-content: center;
  align-items: center;
  font-size: var(--font-size-huge);
  background-color: var(--color-lightpink);
`;

// 제출 박스 컨테이너
const SubmitSection = styled.section`
  width: 100%;
  display: flex;
  justify-content: flex-end;
`;

const IndividualForm = ({ isIndividual = true }) => {
  const [userId, setUserId] = useState("");

  const onSubmitForm = (e) => {
    e.preventDefault();
    const body = {
      userId: userId,
      // userPwd: userPwd,
      // userPwd2: userPwd2,
    };

    // 이미지 전송용
    // const formData = new FormData();
    // formData.append("individualData", data[0]);

    axios
      .post(
        "/wherewego/user/getUserData",
        body
        // , { headers: { "Content-Type": `multipart/form-data` } },
      )
      .then((response) => {
        console.log("response : ", JSON.stringify(response, null, 2));
        window.location.href = "http://localhost:3000/";
      })
      .catch((error) => {
        console.log("failed", error);
      });
  };

  return (
    <Container isVisible={isIndividual}>
      <Form
        id="individual_form"
        onSubmit={onSubmitForm}
        encType="multipart/form-data"
      >
        <TopSection>
          <LeftSection>
            <ItemContainer>
              <Label htmlFor="userId">아이디</Label>
              <Input
                type="text"
                name="userId"
                id="userId"
                required
                minLenght="6"
                maxLength="12"
                pattern="[A-Za-z]{1}\w{5,19}"
                placeholder="ID(6~12)"
              />
              <Button>중복 확인</Button>
            </ItemContainer>
            <ItemContainer>
              <Label>비밀번호</Label>
              <Input type="password" name="userPwd" id="userPwd" />
            </ItemContainer>
            <ItemContainer>
              <Label>비밀번호 확인</Label>
              <Input type="password" name="userPwd2" id="userPwd2" />
            </ItemContainer>
            <ItemContainer>
              <Label>이름</Label>
              <Input type="text" name="userName" id="userName" width="10em" />
            </ItemContainer>
            <ItemContainer>
              <Label>주민등록번호</Label>
              {/* length가 6이 되면 밸류에 하이픈을 추가하는걸로 바꾸자 */}
              <Input type="text" name="userNum1" id="userNum1" width="5em" />
              <Hyphen> - </Hyphen>
              <Input
                type="password"
                name="userNum2"
                id="userNum2"
                width="5em"
              />
            </ItemContainer>
            <PhoneNumContainer>
              <Label>연락처</Label>
              <Input
                type="select"
                name="user_tel1"
                id="user_tel1"
                width="3em"
              />
              <Hyphen> - </Hyphen>
              <Input type="text" name="user_tel2" id="user_tel2" width="3em" />
              <Hyphen> - </Hyphen>
              <Input type="text" name="user_tel3" id="user_tel3" width="3em" />
            </PhoneNumContainer>
            <ItemContainer>
              <Label>이메일</Label>
              <Input type="email" width="20em" />
            </ItemContainer>
            <ItemContainer>
              <Label>우편번호</Label>
              <Input
                type="text"
                name="user_zipcode"
                id="user_zipcode"
                width="4em"
              />
              <Button>우편번호 검색</Button>
            </ItemContainer>
            <ItemContainer>
              <Label>주소</Label>
              <Input type="text" name="user_addr" id="user_addr" width="20em" />
            </ItemContainer>
            <ItemContainer>
              <Label>상세주소</Label>
              <Input
                type="text"
                name="user_addrdetail"
                id="user_addrdetail"
                width="24em"
              />
            </ItemContainer>
            <ItemContainer>
              <Label>기념일</Label>
              <Input type="date" name="user_date" id="user_date" width="12em" />
            </ItemContainer>
          </LeftSection>
          <RightSection>
            <UserImgContainer>
              <i className="far fa-user"></i>
            </UserImgContainer>
            <Button>사진 선택</Button>
          </RightSection>
        </TopSection>

        {/* 바텀 폼 */}
        <BottomSection>
          <ItemContainer>
            <Label>관심사</Label>
            <OptionsContainer>
              <CheckboxLabel>
                <Checkbox type="checkbox" name="user_option" id="" /> 임시
              </CheckboxLabel>
              <CheckboxLabel>
                <Checkbox type="checkbox" name="user_option" /> 로
              </CheckboxLabel>
              <CheckboxLabel>
                <Checkbox type="checkbox" name="user_option" /> 다양한 값을
                입력하여
              </CheckboxLabel>
              <CheckboxLabel>
                <Checkbox type="checkbox" name="user_option" /> 박스의 크기가
                예쁘게 늘어나는지 확인하기 위한
              </CheckboxLabel>
              <CheckboxLabel>
                <Checkbox type="checkbox" name="user_option" /> 체크박스입니다.
              </CheckboxLabel>
              <CheckboxLabel>
                <Checkbox type="checkbox" name="user_option" /> 값은 아직
              </CheckboxLabel>
              <CheckboxLabel>
                <Checkbox type="checkbox" name="user_option" /> 전달되지
                않는다는 사실을 부디 명심하시고
              </CheckboxLabel>
              <CheckboxLabel>
                <Checkbox type="checkbox" name="user_option" /> 값이 전달되지
                않는다고 엄한 노트북에 샷건을 치지 않기를
              </CheckboxLabel>
              <CheckboxLabel>
                <Checkbox type="checkbox" name="user_option" /> 간절하게
                바랍니다.
              </CheckboxLabel>
              <CheckboxLabel>
                <Checkbox type="checkbox" name="user_option" /> 이만
              </CheckboxLabel>
              <CheckboxLabel>
                <Checkbox type="checkbox" name="user_option" /> 아직도 있나?
              </CheckboxLabel>
              <CheckboxLabel>
                <Checkbox type="checkbox" name="user_option" /> 그만하자
              </CheckboxLabel>
              <CheckboxLabel>
                <Checkbox type="checkbox" name="user_option" /> 이정도면 됐지
              </CheckboxLabel>
            </OptionsContainer>
          </ItemContainer>
        </BottomSection>
        <SubmitSection>
          <SubmitButton as="input" type="reset" value="초기화" />
          <SubmitButton as="input" type="submit" value="회원가입" />
        </SubmitSection>
      </Form>
    </Container>
  );
};

export default IndividualForm;
