import React, { useCallback, useEffect, useRef, useState } from "react";
import styled from "styled-components";
import ContentsBox1 from "./sliderSources/ContentsBox1";
import ContentsBox2 from "./sliderSources/ContentsBox2";
import ContentsBox3 from "./sliderSources/ContentsBox3";

const Container = styled.div`
  width: auto;
  display: flex;
  transition-duration: 0.7s;
  transition-timing-function: cubic-bezier(0.57, 0.01, 0.36, 0.99);
  transform: translateX(
    ${(props) => {
      /* console.log(props.idx); */
      return props.idx === "first"
        ? "0"
        : props.idx === "second"
        ? "-100%"
        : "-200%";
    }}
  );
`;

const LinkedContents = () => {
  const [idx, setIdx] = useState("first");
  const interval = useRef();
  // useCallback을 쓰는 이유???
  const changeIdx = useCallback(() => {
    idx === "first"
      ? setIdx("second")
      : idx === "second"
      ? setIdx("third")
      : setIdx("first");
    // console.log(idx);
  }, [idx]);
  useEffect(() => {
    interval.current = setInterval(changeIdx, 7000);
    return () => {
      clearInterval(interval.current);
    };
  }, [idx, changeIdx]);

  return (
    <Container onClick={changeIdx} idx={idx}>
      <div>
        <ImageBox>
          <img
            src={`${process.env.PUBLIC_URL}/images/img_main1.jpg`}
            alt="img_main1"
          />
        </ImageBox>
        <TextBox>
          <Text1 />
        </TextBox>
      </div>
      <div>
        <ImageBox>
          <img
            src={`${process.env.PUBLIC_URL}/images/img_main2.jpg`}
            alt="img_main2"
          />
        </ImageBox>
        <TextBox>
          <Text2 />
        </TextBox>
      </div>
      <div>
        <ImageBox>
          <img
            src={`${process.env.PUBLIC_URL}/images/img_main3.jpg`}
            alt="img_main3"
          />
        </ImageBox>
        <TextBox>
          <Text3 />
        </TextBox>
      </div>
    </Container>
  );
};

export default LinkedContents;
