import React, { useEffect, useRef } from "react";
import styled from "styled-components";

const TMapContainer = styled.div`
  width: 800px;
  height: 400px;
`;

const CustomTMap = () => {
  const mapRef = useRef();
  useEffect(() => {
    console.log(mapRef);
    // 지도 생성
    function initTmap() {
      var map = new Tmapv2.Map("TMapApp", {
        center: new Tmapv2.LatLng(37.566481622437934, 126.98502302169841),
        width: "800px",
        height: "400px",
        zoom: 15,
      });
    }
    initTmap();
    // script.type = "text/javascript";
    // script.async = "async";

    // 시작 심볼
    // const marker_s = new Tmapv2.Marker({
    //   position: new Tmapv2.LatLng(37.5668986, 126.97871544),
    //   icon: "http://tmapapi.sktelecom.com/upload/tmap/marker/pin_r_m_s.png",
    //   iconSize: new Tmapv2.Size(24, 38),
    //   map: map,
    // });
  }, []);

  return (
    <TMapContainer id="TMapApp">
      <script type="text/javascript" async="async" ref={mapRef} />
    </TMapContainer>
  );
};

export default CustomTMap;
