import React, { useState } from "react";
import axios from "axios";
import styled from "styled-components";
import Form from "./Form";
import {
  Button,
  Label,
  SubmitButton,
  ItemContainer,
  Input,
} from "../registrationForm/FormMixin";

const Container = styled.div`
  overflow: hidden;
  transition-delay: ${(props) => {
    return props.isVisible ? "0" : "0.5s";
  }};
  transition-duration: 0.5s;
  height: ${(props) => {
    return props.isVisible ? "0" : "120vh";
  }};
`;

const FlexForm = styled(Form)`
  align-items: flex-start;
`;

// 제출 박스 컨테이너
const SubmitSection = styled.section`
  width: 100%;
  display: flex;
  justify-content: flex-end;
`;

const CompanyForm = ({ isIndividual = true }) => {
  const [test, setTest] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    let body = {
      testData: test,
    };
    axios
      .post("/wherewego/registertest", body)
      .then((response) => console.log(response));
    window.location.href = "http://localhost:3000/";
  };

  const handleTest = (e) => {
    e.preventDefault();
    console.log(e.target.value);
    setTest(e.target.value);
  };

  return (
    <Container isVisible={isIndividual}>
      <FlexForm id="company-form" onSubmit={handleSubmit}>
        <IDContainer>
          <Label>아이디</Label>
          <Input type="text" name="test" />
        </IDContainer>
        <PWDContainer>
          <Label>비밀번호</Label>
          <Input type="text" name="test" />
        </PWDContainer>
        <PWDCheckContainer>
          <Label>비밀번호 확인</Label>
          <Input type="text" name="test" />
        </PWDCheckContainer>
        <NameContainer>
          <Label>상호명</Label>
          <Input type="text" name="test" />
        </NameContainer>
        <PersonalNumContainer>
          <Label>사업자등록번호(3, 2, 5자리)</Label>
          <Input type="text" name="test" />
        </PersonalNumContainer>
        <PhoneNumContainer>
          <Label>대표 연락처</Label>
          <Input type="text" name="test" />
        </PhoneNumContainer>
        <ItemContainer>
          <Label>대표 이메일</Label>
          <Input type="text" name="test" />
        </ItemContainer>
        <ItemContainer>
          <Label>우편번호</Label>
          <Input type="text" name="test" />
        </ItemContainer>
        <ItemContainer>
          <Label>주소</Label>
          <Input type="text" name="test" />
        </ItemContainer>
        <ItemContainer>
          <Label>상세주소</Label>
          <Input type="text" name="test" />
        </ItemContainer>

        <SubmitSection>
          <SubmitButton as="input" type="reset" value="초기화" />
          <SubmitButton as="input" type="submit" value="회원가입" />
        </SubmitSection>
      </FlexForm>
    </Container>
  );
};

export default CompanyForm;
