import React, { useState } from "react";
import styled from "styled-components";

const Container = styled.div`
  display: flex;
  justify-content: center;
  transition-duration: 0.7s;
  transition-timing-function: cubic-bezier(0.57, 0.01, 0.36, 0.99);
  transform: translateX(${(props) => props.idx * -50 + "%"});
`;

const PageSlider = ({ children, ...props }) => {
  const [idx, setIdx] = useState("first");

  return <Container idx={props.idx}>{children}</Container>;
};

export default PageSlider;
