import React, { useState } from "react";
import Calendar from "react-calendar";
import "react-calendar/dist/Calendar.css";
import styled from "styled-components";

const CustomCSS = styled.div`
  /* 캘릭더 틀 */
  .react-calendar {
    height: 320px;
    background-color: inherit;
    font-family: inherit;
    border: none;
  }

  /* 최상단 네비게이션 버튼 */
  .react-calendar__navigation button:not(.react-calendar__navigation__arrow) {
    font-family: inherit;
  }
  .react-calendar__navigation button:enabled:hover,
  .react-calendar__navigation button:enabled:focus {
    background-color: inherit;
  }

  /* 요일 */
  abbr[title] {
    text-decoration: none;
  }

  /* 년도, 월, 일 선택 버튼 */
  .react-calendar__decade-view__years__year:focus,
  .react-calendar__decade-view__years__year:hover,
  .react-calendar__year-view__months__month:focus,
  .react-calendar__year-view__months__month:hover,
  .react-calendar__month-view__days__day:focus,
  .react-calendar__month-view__days__day:hover {
    background-color: inherit;
  }
`;

const CustomCalerdar = () => {
  const [value, onChange] = useState(new Date());
  return (
    <CustomCSS>
      <Calendar onChange={onChange} value={value} />
    </CustomCSS>
  );
};

export default CustomCalerdar;
