import React, { useEffect, useState } from "react";
import styled from "styled-components";

const ToggleBackground = styled.div`
  width: 54px;
  height: 32px;
  background-color: var(--color-green);
  border-radius: 16px;
  display: flex;
  align-items: center;
  transition-duration: 0.3s;
  &:hover {
    cursor: pointer;
  }
`;

const ToggleCircle = styled.div`
  width: 28px;
  height: 28px;
  background-color: hsl(0, 0%, 70%);
  /* border: 1px solid var(--color-dark-green); */
  border-radius: 50%;
  /* box-shadow: -4px 0px 2px 2px hsl(0, 0%, 20%); */
  transition-duration: 0.3s;
  transform: translateX(${(props) => props.toggleOn * 26 + "px"});
  position: relative;

  &:before {
    content: ${(props) => props.content};
    font-size: var(--font-size-tiny);
    position: relative;
    top: -28px;
  }
  /* &:after {
    content: "";
    font-size: var(--font-size-tiny);
    position: relative;
    top: -48px;
    left: 32px;
  } */
`;

const ToggleButton = ({ setClassification = () => {} }) => {
  const [toggleOn, setToggleOn] = useState(0);
  const [content, setContent] = useState("개인");

  useEffect(() => {
    toggleOn === 0
      ? setClassification("individual")
      : setClassification("company");
  }, [toggleOn]);

  const onClickButton = () => {
    // 0일때 개인, 1일때 사업자
    toggleOn === 0 ? setToggleOn(1) : setToggleOn(0);
    content === "개인" ? setContent("법인") : setContent("개인");
  };

  return (
    <ToggleBackground onClick={onClickButton}>
      <ToggleCircle toggleOn={toggleOn} content={content} />
    </ToggleBackground>
  );
};

export default ToggleButton;
