import { useState } from "react";

const INVISIBLE = "0";
const VISIBLE = "60px";

const UseToggle = () => {
  const [visiblity, setVisiblity] = useState(INVISIBLE);
  const setVisible = () => setVisiblity(VISIBLE);
  const setInvisible = () => setVisiblity(INVISIBLE);
  const toggleVisiblity = () => {
    visiblity === INVISIBLE ? setVisible() : setInvisible();
  };

  return { setVisible, setInvisible, toggleVisiblity };
};

export default UseToggle;
