      ? "식당카페기타"
      : matchedData.coursesort === 1
      ? "식당카페기타"
      : null;

  // sort1 + sort2 + sort3 === "식당카페기타"
  //   ? 1
  //   : sort1 + sort2 + sort3 === "식당기타카페"
  //   ? 2
  //   : sort1 + sort2 + sort3 === "카페식당기타"
  //   ? 3
  //   : sort1 + sort2 + sort3 === "카페기타식당"
  //   ? 4
  //   : sort1 + sort2 + sort3 === "기타식당카페"
  //   ? 5
  //   : sort1 + sort2 + sort3 === "기타카페식당"
  //   ? 6
  //   : null;

  useEffect(() => {
    axios
      .post("/wherewego/userCourseDetail", { c_num: cnum })
      .then((response) => {
        console.log(response.data);
        setMatchedData(response.data);
        setIsLoaded(true);
      })
      .catch((error) => console.log(error));
  }, []);
  return (
    <>
      <Header />
      <BodyLayout>
        <MenuTitle>{isLoaded ? matchedData?.name : null}</MenuTitle>
        <Container>
          <ContentsBox>
            <CourseBox>
              <ImageBox>
                <img
