                />
              </ImageBox>
              {matchedData?.name1} -> {matchedData?.name2} ->
              {matchedData?.name3}
              역명: {matchedData?.stname}
            </CourseBox>
          </ContentsBox>
        </Container>
      </BodyLayout>
      <Footer />
    </>
  );
};

export default MyCourseItem;
