import React, { useEffect, useState } from "react";
import styled from "styled-components";
import { Link } from "react-router-dom";
import Header from "../components/header/Header";
import Footer from "../components/footer/Footer";
import { Button, BodyLayout, MenuTitle } from "../components/body/mixin/Mixin";
import PageSlider from "../components/body/mixin/PageSlider";
import CustomTMap from "../components/body/map/CustomTMap";
import GoogleMapSettings from "../components/body/map/GoogleMapSettings";

const Slider = styled.div`
  width: 100vw;
  overflow: hidden;
  display: flex;
  justify-content: flex-start;
`;

const Container = styled.div`
  width: 100vw;
  display: flex;
  flex-direction: column;
  align-items: center;
  & h1 {
    margin: var(--margin-default) 0;
  }
`;

const ButtonContainer = styled.div`
  width: 200px;
  display: flex;
  justify-content: center;
`;

const DetermineButtons = styled.div`
  width: 400px;
  display: flex;
  justify-content: center;
`;

const LineContianer = styled.div`
  width: 800px;
  height: 40px;
  margin-top: var(--margin-default);
  display: flex;
  align-items: center;
`;

const LineSpan = styled.span`
  width: 370px;
  height: 4px;
  background-color: var(--color-focus);
  position: relative;
  right: 2px;
`;

const Circle = styled.div`
  width: 32px;
  height: 32px;
  display: flex;
  justify-content: center;
  align-items: center;
  position: relative;
  border: 4px solid var(--color-focus);
  border-radius: 50%;
  right: 2px;
  font-weight: bold;
  background-color: ${(props) => props.bgColor};
  color: ${(props) => props.fontColor};
  &:hover {
    cursor: pointer;
    color: var(--color-dark-focus);
    background-color: var(--color-blur);
  }
`;

const PlaceInfoWindow = styled.div`
  width: 800px;
  height: 400px;
  display: flex;
`;
const ImageSection = styled.div`
  width: 400px;
  height: 400px;
  overflow: hidden;
  border-radius: 12px 0 0 12px;
  & img {
    width: 100%;
    height: 100%;
  }
`;

const InfoSection = styled.div`
  width: 400px;
  height: 400px;
  padding: var(--padding-default);
  border: 2px solid var(--color-green);
  border-radius: 0 12px 12px 0;
  background-color: var(--color-light-bg);
`;

// 코스 정보 받아와서 > 장소1, 장소2, 장소3 주소를 티맵으로 보내서 찍기
const Result = ({ location }) => {
  const [idx, setIdx] = useState(0);
  const [startPoint, setStartPoint] = useState();
  // "서울특별시 마포구 서교동 332 33 1층"
  const [wayPoint, setWayPoint] = useState();
  // "서울특별시 마포구 서교동 홍익로6길 15"
  const [endPoint, setEndPoint] = useState();
  // "서울특별시 마포구 서교동 와우산로21길 31-10"

  // 장소 표시하기
  const [isSelected1, setIsSelected1] = useState(false);
  const [isSelected2, setIsSelected2] = useState(false);
  const [isSelected3, setIsSelected3] = useState(false);

  // 검색 결과 받아와서 찍기
  const courseResults = location.props.result;
  console.log(courseResults);
  useEffect(() => {
    // console.log("코스 검색을 통해 들어오면 마운트, 각 포인트 설정");
    // console.log(courseResults[0]?.addr1);
    // console.log(courseResults[0]?.addr2);
    // console.log(courseResults[0]?.addr3);
    setStartPoint(courseResults[0]?.addr1);
    setWayPoint(courseResults[0]?.addr2);
    setEndPoint(courseResults[0]?.addr3);
  }, [courseResults]);
  // console.log("startPoint: ", startPoint);
  // console.log("wayPoint: ", wayPoint);
  // console.log("endPoint: ", endPoint);
  // 구글맵에서 지오코딩하기
  const coords = GoogleMapSettings({ startPoint, wayPoint, endPoint });
  // console.log("coords[0]: ", coords[0]);
  // console.log("coords[1]: ", coords[1]);
  // console.log("coords[2]: ", coords[2]);

  const onClickPlace1 = (e) => {
    setIsSelected1(true);
    setIsSelected2(false);
    setIsSelected3(false);
  };
  const onClickPlace2 = (e) => {
    setIsSelected1(false);
    setIsSelected2(true);
    setIsSelected3(false);
  };
  const onClickPlace3 = (e) => {
    setIsSelected1(false);
    setIsSelected2(false);
    setIsSelected3(true);
  };

  const onClickNext = () => {
    setIdx((prevIdx) => prevIdx + 1);
  };
  const onClickPrev = () => {
    setIdx((prevIdx) => prevIdx - 1);
  };
  // console.log("coords: ", coords);

  return (
    <>
      <Header />
      <BodyLayout>
        <MenuTitle>{courseResults[0].name}</MenuTitle>
        {coords.length === 3 ? (
          <CustomTMap
            startPoint={coords[0]}
            wayPoint={coords[1]}
            endPoint={coords[2]}
          />
        ) : null}
        <Slider>
          <PageSlider idx={idx} rate={-25}>
            <Container>
              <LineContianer>
                <Circle
                  onClick={onClickPlace1}
                  bgColor={isSelected1 ? "var(--color-focus)" : "white"}
                  fontColor={isSelected1 ? "white" : "var(--color-font)"}
                >
                  1
                </Circle>
                <LineSpan />
                <Circle
                  onClick={onClickPlace2}
                  bgColor={isSelected2 ? "var(--color-focus)" : "white"}
                  fontColor={isSelected2 ? "white" : "var(--color-font)"}
                >
                  2
                </Circle>
                <LineSpan />
                <Circle
                  onClick={onClickPlace3}
                  bgColor={isSelected3 ? "var(--color-focus)" : "white"}
                  fontColor={isSelected3 ? "white" : "var(--color-font)"}
                >
                  3
                </Circle>
              </LineContianer>
              {isSelected1 ? (
                <>
                  <h1>{courseResults[0]?.name1}</h1>
                  <PlaceInfoWindow>
                    <ImageSection>
                      <img
                        src={`${process.env.PUBLIC_URL}/images/places/food.jpg`}
                        alt="first"
                      />
                    </ImageSection>
                    <InfoSection>
                      <h2>{courseResults[0]?.name1}</h2>
                      <h3>주소: {courseResults[0]?.addr1}</h3>
                      <h3>연락처: {courseResults[0]?.tel1}</h3>
                      <h3>링크: {courseResults[0]?.link1}</h3>
                      <h3>기타: {courseResults[0]?.info1}</h3>
                    </InfoSection>
                  </PlaceInfoWindow>
                </>
              ) : isSelected2 ? (
                <>
                  <h1>{courseResults[0]?.name2}</h1>
                </>
              ) : isSelected3 ? (
                <>
                  <h1>{courseResults[0]?.name3}</h1>
                </>
              ) : null}
            </Container>
            <Container>
              <div>날씨 소개</div>
              <div>추천 코디</div>
            </Container>
          </PageSlider>
        </Slider>
        {idx === 0 ? (
          <ButtonContainer>
            <Button onClick={onClickNext}>다음</Button>
          </ButtonContainer>
        ) : idx === 1 ? (
          <ButtonContainer>
            <Button onClick={onClickPrev}>이전</Button>
          </ButtonContainer>
        ) : null}

        <DetermineButtons>
          <Link to={"/planning"}>
            <Button>다시 선택하기</Button>
          </Link>
