import React from "react";
import { Link } from "react-router-dom";
import styled from "styled-components";

const FooterContainer = styled.footer`
  width: 100%;
  height: var(--footer-height);
  background-color: var(--color-blur);
  color: var(--color-font);
  display: flex;
  justify-content: center;
`;

const NavList = styled.nav`
  width: 48%;
  display: flex;
  justify-content: space-between;
`;

const NavItemsContainer = styled.div`
  margin: var(--margin-default) 0;
`;

const NavItemsTitle = styled.ul`
  font-size: var(--font-size-small-title);
`;

const NavItems = styled.li`
  margin-top: calc(var(--margin-default) / 4);
  font-size: var(--font-size-small);
  :first-child {
    margin-top: calc(var(--margin-default) / 2);
  }
  :hover {
    cursor: pointer;
    color: var(--color-warning);
  }
`;

const Footer = () => {
  return (
    <FooterContainer>
      <NavList>
        <NavItemsContainer>
          <NavItemsTitle>
            소개
            <NavItems>
              <Link to={`/Notice`}>공지사항</Link>
            </NavItems>
            <NavItems>
              <Link to={`/about`}>서비스 안내</Link>
            </NavItems>
            <NavItems>이용 방법</NavItems>
          </NavItemsTitle>
        </NavItemsContainer>
        <NavItemsContainer>
          <NavItemsTitle>
            고객센터
            <NavItems>이용약관</NavItems>
            <NavItems>개인정보취급방침</NavItems>
            <NavItems>
              <Link to={`/faq`}>FAQ</Link>
            </NavItems>
            <NavItems>Q&A</NavItems>
          </NavItemsTitle>
        </NavItemsContainer>
        <NavItemsContainer>
          <NavItemsTitle>
            광고
            <NavItems>
              <Link to={`/adforad`}>광고 문의</Link>
            </NavItems>
          </NavItemsTitle>
        </NavItemsContainer>
        <NavItemsContainer>
          <NavItemsTitle>
            커뮤니티
            <NavItems>
              <Link to={`/Reviews`}>후기</Link>
            </NavItems>
            <NavItems>
              <Link to={`/UserRecommendation`}>코스 제안</Link>
            </NavItems>
          </NavItemsTitle>
        </NavItemsContainer>
      </NavList>
    </FooterContainer>
  );
};

export default Footer;
