import React, { useState } from "react";
import styled from "styled-components";

const LineContainer = styled.div`
  width: 600px;
  display: flex;
  justify-content: space-evenly;
  font-size: var(--font-size-normal);
  position: relative;
  top: -52px;
  left: 220px;
`;

const LineCircle = styled.div`
  width: 40px;
  height: 40px;
  border-radius: 50%;
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: ${(props) => props.bgColor};
  color: white;
  &:hover {
    cursor: pointer;
    transform: scale(1.1);
    transition-duration: 0.2s;
  }
`;

const LineSelector = ({ setLineNum = () => {} }) => {
  const [bgColor, setBgColor] = useState("");
  const onClickNum = (e) => {
    console.log(e);
    console.log(e.target);
    console.log(e.target.innerText);
    setLineNum(e.target.innerText);
    setBgColor(`var(--color-line${lineNum})`);
  };
  return (
    <LineContainer>
      <LineCircle onClick={onClickNum}>1</LineCircle>
      <LineCircle onClick={onClickNum}>2</LineCircle>
      <LineCircle onClick={onClickNum}>3</LineCircle>
      <LineCircle onClick={onClickNum}>4</LineCircle>
      <LineCircle onClick={onClickNum}>5</LineCircle>
      <LineCircle onClick={onClickNum}>6</LineCircle>
      <LineCircle onClick={onClickNum}>7</LineCircle>
      <LineCircle onClick={onClickNum}>8</LineCircle>
      <LineCircle onClick={onClickNum}>9</LineCircle>
    </LineContainer>
  );
};

export default LineSelector;
