import React, { useState } from "react";
import styled from "styled-components";
import Header from "../components/header/Header";
import { BodyLayout } from "../components/body/mixin/Mixin";
import Footer from "../components/footer/Footer";
import {
  Button,
  Input,
  MenuTitle,
  SearchBar,
} from "../components/body/mixin/Mixin";
import FAQData from "../server/FAQData.json";
import UseToggle from "../hooks/UseToggle";

const NoticeContainer = styled.ul`
  width: 1200px;
  height: 800px;
  overflow: hidden;
`;

const NoticeItem = styled.li`
  width: 100%;
  min-height: 60px;
  border-bottom: 1px solid black;
  overflow: hidden;
  text-align: center;
  &:first-child {
    font-size: var(--font-size-title-small);
    background-color: var(--color-focus);
  }
  i:hover {
    cursor: pointer;
    color: var(--color-focus);
  }
`;

const Liner = styled.div`
  display: flex;
  align-items: center;
  text-align: center;
  line-height: 60px;
`;

const SmallBox = styled.div`
  width: 12%;
  transform: rotate(${(props) => props.arrowRotation});
  transition-duration: 0.3s;
`;

const TitleBox = styled.div`
  width: 60%;
  padding-left: 4%;
`;

const ContentBox = styled.div`
  width: calc(100% - 132px);
  height: ${(props) => props.fullHeight || "0"};
  margin-left: 132px;
  padding-right: var(--margin-default);
  line-height: 30px;
  border-top: 1px solid hsl(0, 0%, 90%);
  font-size: var(--font-size-small);
  text-align: left;
  transition-duration: 0.3s;
`;

const ItemTitleBox = styled(TitleBox)`
  text-align: left;
  overflow: hidden;
  white-space: nowrap;
  text-overflow: ellipsis;
  transition: height;
  transition-duration: 0.3s;
  :hover {
    cursor: pointer;
    color: var(--color-focus);
  }
`;

const FAQ = () => {
  const [fullHeight, setFullHeight] = useState("0");
  const [arrowRotation, setArrowRotation] = useState("0");
  const { toggleState } = UseToggle();
  console.log(toggleState);
  const openItem = (e) => {
    fullHeight === "0" ? setFullHeight("60px") : setFullHeight("0");
    fullHeight === "0" ? setArrowRotation("0.5turn") : setArrowRotation("0");
    console.log(fullHeight);
    console.log(arrowRotation);
    console.log(e);
    console.log(e.target);
    console.log(e.target.dataset.key);
  };

  return (
    <>
      <Header />
      <BodyLayout>
        <MenuTitle>FAQ</MenuTitle>
        <NoticeContainer>
          <NoticeItem>
            <Liner>
              <SmallBox>번호</SmallBox>
              <SmallBox>분류</SmallBox>
              <TitleBox>제목</TitleBox>
              <SmallBox>작성일</SmallBox>
              <SmallBox></SmallBox>
            </Liner>
          </NoticeItem>
          {FAQData.items.map((item) => {
            return (
              <>
                <NoticeItem>
                  <Liner>
                    <SmallBox>{item.number}</SmallBox>
                    <SmallBox>{item.class}</SmallBox>
                    <ItemTitleBox onClick={openItem} data-key={item.number}>
                      {item.title}
                    </ItemTitleBox>
                    <SmallBox>{item.enrollment}</SmallBox>
                    <SmallBox onClick={openItem} arrowRotation={arrowRotation}>
                      <i
                        className="fas fa-chevron-down"
                        data-key={item.number}
                      ></i>
                    </SmallBox>
                  </Liner>
                  <ContentBox fullHeight={fullHeight}>
                    {item.content}
                  </ContentBox>
                </NoticeItem>
              </>
            );
          })}
        </NoticeContainer>
        <SearchBar>
          <Input type="text" placeholder="검색하세요" />
          <Button>
            <i className="fas fa-search"></i>
          </Button>
        </SearchBar>
      </BodyLayout>
      <Footer />
    </>
  );
};

export default FAQ;
