import React, { useEffect, useState } from "react";
import styled from "styled-components";

const ToggleBackground = styled.div`
  width: 80px;
  height: 32px;
  background-color: var(--color-focus);
  border-radius: 16px;
  display: flex;
  align-items: center;
  transition-duration: 0.3s;
  &:hover {
    cursor: pointer;
  }
`;

const ToggleCircle = styled.div`
  width: 28px;
  height: 28px;
  background-color: white;
  /* border: 1px solid var(--color-dark-green); */
  border-radius: 50%;
  /* box-shadow: -4px 0px 2px 2px hsl(0, 0%, 20%); */
  transition-duration: 0.3s;
  transform: translateX(${(props) => props.toggleOn * 60 + "px"});
  position: relative;
`;

const Text = styled.div`
  font-size: var(--font-size-tiny);
  position: relative;
  top: 6px;
  right: 24px;
`;

const ToggleButton = ({ setClassification = () => {} }) => {
  const [toggleOn, setToggleOn] = useState(0);

  useEffect(() => {
    toggleOn === 0
      ? setClassification("individual")
      : setClassification("company");
  }, [toggleOn]);

  const onClickButton = () => {
    // 0일때 개인, 1일때 사업자
    toggleOn === 0 ? setToggleOn(1) : setToggleOn(0);
  };

  return (
    <ToggleBackground onClick={onClickButton}>
      <ToggleCircle toggleOn={toggleOn}>
        <Text>{toggleOn === 0 ? "개인" : "법인"}</Text>
      </ToggleCircle>
    </ToggleBackground>
  );
};

export default ToggleButton;
