import { useEffect, useState } from "react";

const UseToggleBGColor = () => {
  const [color, setColor] = useState("");
  const setFocusColor = () => setColor("var(--color-focus)");
  const setNormalColor = () => setColor("");
  const toggleBGColor = () => {
    color === "" ? setFocusColor() : setNormalColor();
  };
  return { color };
};

export default UseToggleBGColor;
