import React, { useRef } from "react";
import styled from "styled-components";
import Header from "../components/header/Header";
import BodyLayout from "../components/body/BodyLayout";
import Footer from "../components/footer/Footer";
import Users from "../server/Users";
import { Link } from "react-router-dom";

const ReviewsContainer = styled.ul`
  width: 80vw;
  height: 80vh;
  margin-top: var(--margin-header-to-body);
  display: flex;
  align-items: center;
  overflow-x: hidden;
  overflow-y: hidden;
  white-space: nowrap;
  user-select: none;
  transition: all 0.2s;
  transform: scale(0.98);
  will-change: transform;
  perspective: 400px;
`;

const ReviewItem = styled.li`
  width: auto;
  height: 80%;
  /* margin: 0 var(--margin-default); */

  &:nth-child(even) {
    transform: scaleX(1.3) rotateY(10deg);
  }
  &:nth-child(odd) {
    transform: scaleX(1.3) rotateY(-10deg);
  }

  /* 체크용 > 삭제 4번 */
  background-color: var(--concept-color4);
`;

const ProfileBox = styled.div`
  width: 200px;
  height: 200px;
`;

const Reviews = () => {
  const UsersData = Users;
  const isDown = useRef(false);
  const sliderRef = useRef();
  let startX;
  let scrollL;

  const startDraging = (e) => {
    // console.log("start~!");
    isDown.current = true;
    startX = e.pageX - sliderRef.current.offsetLeft;
    scrollL = sliderRef.current.scrollLeft;
  };

  const slideReviews = (e) => {
    // console.log("moving!", isDown);
    if (!isDown.current) return;
    e.preventDefault();
    const x = e.pageX - sliderRef.current.offsetLeft;
    const walk = (x - startX) * 2;
    sliderRef.current.scrollLeft = scrollL - walk;
  };

  const stopDraging = () => {
    // console.log("stop...", isDown);
    isDown.current = false;
  };

  const showDetailItem = () => {};

  return (
    <>
      <Header />
      <BodyLayout>
        <ReviewsContainer
          ref={sliderRef}
          onMouseDown={startDraging}
          onMouseMove={slideReviews}
          onMouseUp={stopDraging}
        >
          <ReviewItem onClick={showDetailItem}>
            <ProfileBox>{UsersData}</ProfileBox>
          </ReviewItem>
          <ReviewItem>
            <ProfileBox>
              <a href="http://localhost:9090/wherewego">testing connect</a>
            </ProfileBox>
          </ReviewItem>
          <ReviewItem>
            <ProfileBox>
              <Link to={"/users"}>테스트용</Link>
            </ProfileBox>
          </ReviewItem>
          <ReviewItem>
            <ProfileBox>3</ProfileBox>
          </ReviewItem>
          <ReviewItem>
            <ProfileBox>4</ProfileBox>
          </ReviewItem>
          <ReviewItem>
            <ProfileBox>5</ProfileBox>
          </ReviewItem>
          <ReviewItem>
            <ProfileBox>6</ProfileBox>
          </ReviewItem>
          <ReviewItem>
            <ProfileBox>7</ProfileBox>
          </ReviewItem>
          <ReviewItem>
            <ProfileBox>8</ProfileBox>
          </ReviewItem>
          <ReviewItem>
            <ProfileBox>9</ProfileBox>
          </ReviewItem>
          <ReviewItem>
            <ProfileBox>10</ProfileBox>
          </ReviewItem>
        </ReviewsContainer>
      </BodyLayout>
      <Footer />
    </>
  );
};

export default Reviews;
