import React, { useState } from "react";
import styled from "styled-components";
import Header from "../components/header/Header";
import { BodyLayout } from "../components/body/mixin/Mixin";
import Footer from "../components/footer/Footer";
import {
  Button,
  Input,
  MenuTitle,
  SearchBar,
} from "../components/body/mixin/Mixin";
import FAQData from "../server/FAQData.json";

const NoticeContainer = styled.ul`
  width: 1200px;
  height: 800px;
  overflow: hidden;
`;

const NoticeItem = styled.li`
  width: 100%;
  height: 60px;
  border-bottom: 1px solid black;
  display: flex;
  align-items: center;
  text-align: center;
  :first-child {
    font-size: var(--font-size-title-small);
    background-color: var(--color-yellow);
  }
`;

const SmallBox = styled.div`
  width: 10%;
`;

const TitleBox = styled.div`
  width: 64%;
`;

const ContentBox = styled.div`
  width: 64%;
  height: 0;
`;

const ItemTitleBox = styled(TitleBox)`
  text-align: left;
  overflow: hidden;
  white-space: nowrap;
  text-overflow: ellipsis;
  :hover {
    cursor: pointer;
    color: var(--color-pink);
  }
`;

const FAQ = () => {
  const [open, setOpen] = useState();
  const openItem = () => {
    setOpen(open);
  };

  console.log(FAQData.items);
  return (
    <>
      <Header />
      <BodyLayout>
        <MenuTitle>FAQ</MenuTitle>
        <NoticeContainer>
          <NoticeItem>
            <SmallBox>번호</SmallBox>
            <SmallBox>분류</SmallBox>
            <TitleBox>제목</TitleBox>
            <SmallBox>작성일</SmallBox>
          </NoticeItem>
          {FAQData.items.map((item) => {
            return (
              <NoticeItem>
                <SmallBox>{item.number}</SmallBox>
                <SmallBox>{item.class}</SmallBox>
                <ItemTitleBox>{item.title}</ItemTitleBox>
                <SmallBox>{item.enrollment}</SmallBox>
              </NoticeItem>
            );
          })}
        </NoticeContainer>
        <SearchBar>
          <Input type="text" placeholder="검색하세요" />
          <Button>
            <i className="fas fa-search"></i>
          </Button>
        </SearchBar>
      </BodyLayout>
      <Footer />
    </>
  );
};

export default FAQ;
