import React from "react";
import { Link } from "react-router-dom";
import styled from "styled-components";
import { StyledButton } from "../mixin/Mixin";

const ItemBox = styled.div`
  width: 100%;
  padding: var(--padding-default);
  display: flex;
  justify-content: space-between;
  align-items: center;

  &:hover {
    cursor: pointer;
    background-color: var(--color-bg);
  }

  &:active {
    background-color: var(--color-blur);
  }

  & > div {
    display: flex;
    align-items: center;
    & > i {
      color: var(--color-focus);
      margin-right: calc(var(--margin-default) / 4);
    }
  }
`;

const MyCourse = (userCourse = null) => {
  const userCourseData = userCourse?.userCourse;
  // console.log("마이코스", userCourse);
  // console.log("옵셔널 체이닝 후 코스", userCourseData);
  return (
    <>
      {userCourseData?.length > 0 ? (
        userCourseData.map((data) => {
          return (
            <ItemBox>
              <div>
                <i className="fas fa-map-marker-alt"></i>
                <p>{data.name}</p>
              </div>
              <p>{data.grade}</p>
            </ItemBox>
          );
        })
      ) : (
        <ItemBox>
          코스를 추천해 주세요
          <Link to={`/userrecommendation`}>
            <StyledButton>추천하러 가기</StyledButton>
          </Link>
        </ItemBox>
      )}
    </>
  );
};

export default MyCourse;
