import React, { useState } from "react";
import styled from "styled-components";
import Header from "../components/header/Header";
import { BodyLayout } from "../components/body/mixin/Mixin";
import Footer from "../components/footer/Footer";
import {
  Button,
  Input,
  MenuTitle,
  SearchBar,
} from "../components/body/mixin/Mixin";

const NoticeContainer = styled.ul`
  width: 1200px;
  height: 800px;
  overflow: hidden;
`;

const NoticeItem = styled.li`
  width: 100%;
  height: 60px;
  border-bottom: 1px solid black;
  display: flex;
  align-items: center;
  text-align: center;
  :first-child {
    font-size: var(--font-size-title-small);
    background-color: var(--color-yellow);
  }
`;

const SmallBox = styled.div`
  width: 10%;
  i {
    font-size: var(--font-size-large);
  }
  i:hover {
    cursor: pointer;
    color: var(--color-pink);
  }
`;

const TitleBox = styled.div`
  width: 60%;
`;

const ItemTitleBox = styled(TitleBox)`
  text-align: left;
  overflow: hidden;
  white-space: nowrap;
  text-overflow: ellipsis;
  :hover {
    cursor: pointer;
    color: var(--color-pink);
  }
`;

const FAQ = () => {
  const [open, setOpen] = useState();
  const openItem = () => {
    setOpen(open);
  };

  return (
    <>
      <Header />
      <BodyLayout>
        <MenuTitle>FAQ</MenuTitle>
        <NoticeContainer>
          <NoticeItem>
            <SmallBox>번호</SmallBox>
            <TitleBox>제목</TitleBox>
            <SmallBox>작성일</SmallBox>
          </NoticeItem>
          <NoticeItem>
            <SmallBox>1</SmallBox>
            <ItemTitleBox>제목이 길어져도 다 수용할수 있어야 함</ItemTitleBox>
            <SmallBox>2021.09.21</SmallBox>
          </NoticeItem>
          <NoticeItem>
            <SmallBox>2</SmallBox>
            <ItemTitleBox>
              그래서 테스트용으로 글을 길게 써 보겠습니다
            </ItemTitleBox>
            <SmallBox>작성일</SmallBox>
          </NoticeItem>
          <NoticeItem>
            <SmallBox>3</SmallBox>
            <ItemTitleBox onClick={openItem}>
              흐헤헤흐헤헤흐헤헤흐헤헤흐헤헤흐헤헤흐헤헤흐헤헤흐헤헤흐헤헤흐헤헤흐헤헤흐헤헤흐헤헤흐헤헤흐헤헤흐헤헤흐헤헤흐헤헤흐헤헤흐헤헤흐헤헤흐헤헤흐헤헤흐헤헤흐헤헤
            </ItemTitleBox>
            <SmallBox>작성일</SmallBox>
          </NoticeItem>
          <NoticeItem>
            <SmallBox>4</SmallBox>
            <ItemTitleBox>
              그냥 보면 알겠지만 공지사항 복붙한 것에 불과함
            </ItemTitleBox>
            <SmallBox>작성일</SmallBox>
          </NoticeItem>
          <NoticeItem>
            <SmallBox>5</SmallBox>
            <ItemTitleBox>하지만 얘는 공지사항과 다르게 </ItemTitleBox>
            <SmallBox>작성일</SmallBox>
          </NoticeItem>
          <NoticeItem>
            <SmallBox>6</SmallBox>
            <ItemTitleBox>이런거 필요없고 제목/내용만 있으면 됨</ItemTitleBox>
            <SmallBox>작성일</SmallBox>
          </NoticeItem>
          <NoticeItem>
