import React, { useState } from "react";
import styled from "styled-components";
import { Link } from "react-router-dom";
import Header from "../components/header/Header";
import Footer from "../components/footer/Footer";
import KakaoMapResult from "../components/body/map/KakaoMapResult";
import { Button, BodyLayout } from "../components/body/mixin/Mixin";
import PageSlider from "../components/body/mixin/PageSlider";

const Slider = styled.div`
  width: 100vw;
  overflow: hidden;
  display: flex;
  justify-content: flex-start;
`;

// 여기도 페이지 슬라이더 먹이자
const Result = () => {
  const [idx, setIdx] = useState(0);
  const onClickNext = () => {
    setIdx((prevIdx) => prevIdx + 1);
  };
  const onClickPrev = () => {
    setIdx((prevIdx) => prevIdx - 1);
  };

  return (
    <>
      <Header />
      <BodyLayout>
        {/* 요약 */}
        <div>
          <div>잠실역 데이트</div>
          <div>10:00~14:00</div>
          <div>잠실역</div>
        </div>
        <Slider>
          <PageSlider idx={idx}>
            <div>소제목</div>
            <KakaoMapResult />
            <div>장소 소개</div>
          </PageSlider>
        </Slider>
        <Button onClick={onClickNext}>다음</Button>
        <Button onClick={onClickPrev}>이전</Button>
        <Link to={"/planning"}>
          <Button>다시 선택하기</Button>
        </Link>
        <Button>휴대폰 알람 설정하기</Button>
      </BodyLayout>
      <Footer />
    </>
  );
};

export default Result;
