import React from "react";
import styled from "styled-components";
import Header from "../components/header/Header";
import BodyLayout from "../components/body/BodyLayout";
import Footer from "../components/footer/Footer";
import ImageSlider from "../components/body/imageSlider/ImageSlider";
import { YellowD } from "../components/body/mixin/Mixin";
import CustomCalerdar from "../components/body/CustomCalerdar";

const MenuSection = styled.section`
  width: 60vw;
  height: 100vh;
  margin: var(--margin-default);
  display: flex;
  justify-content: center;
  flex-wrap: wrap;
`;

const Container = styled.div`
  width: 40%;
  min-width: 360px;
  height: 48%;
  margin: var(--margin-default);
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  background-color: var(--color-lightpink);
  border-radius: 8px;
`;

const SubHeading = styled.div`
  margin: calc(var(--margin-default) / 2) 0;
  font-size: var(--font-size-title-normal);
`;

const Article = styled.article`
  width: 100%;
  height: calc(100% - var(--margin-default) - var(--font-size-title-normal));
  padding: var(--padding-default);
  font-size: var(--font-size-large);
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const CustomCalendarWrapper = styled(Article)`
  /* 캘릭더 틀 */
  .react-calendar {
    height: 320px;
    background-color: inherit;
    font-family: inherit;

    border: none;
  }

  /* 최상단 네비게이션 버튼 */
  .react-calendar__navigation button:not(.react-calendar__navigation__arrow) {
    font-family: inherit;
  }
  .react-calendar__navigation button[disabled],
  .react-calendar__navigation button:enabled:hover,
  .react-calendar__navigation button:enabled:focus {
    background-color: inherit;
  }

  /* 요일 */
  abbr[title] {
    text-decoration: none;
  }

  /* 10년, 년도, 월, 일 선택 버튼 */
  .react-calendar__century-view__decades__decade:focus,
  .react-calendar__century-view__decades__decade:hover,
  .react-calendar__decade-view__years__year:focus,
  .react-calendar__decade-view__years__year:hover,
  .react-calendar__year-view__months__month:focus,
  .react-calendar__year-view__months__month:hover,
  .react-calendar__month-view__days__day:focus,
  .react-calendar__month-view__days__day:hover {
    background-color: inherit;
  }

  .react-calendar__tile {
    font-family: inherit;
    width: 45px;
    height: 45px;
    border-radius: 50%;
  }
`;

const ArticleItem = styled.div`
  margin: calc(var(--margin-default) / 2) calc(var(--margin-default) / 4);
`;

const MemberHome = () => {
  return (
    <>
      <Header />
      <BodyLayout>
        <ImageSlider />
        <MenuSection>
          <Container>
            <SubHeading>
              <YellowD>D</YellowD>+ 36,500
            </SubHeading>
            <Article>
              {/* 시작일 받아서 반복문 */}
              <ArticleItem>36,600 기념일 D-100 (2031/12/10)</ArticleItem>
              <ArticleItem>36,700 기념일 D-200 (2032/03/20)</ArticleItem>
              <ArticleItem>36,800 기념일 D-300 (2032/06/30)</ArticleItem>
              <ArticleItem>101주년 D-365 (2032/09/05)</ArticleItem>
            </Article>
          </Container>
          <Container>
            <SubHeading>플랜</SubHeading>
            {/* 예약시에는(예약일) minDate 오늘, 가입시에는(시작일) maxDate 오늘 */}
            <CustomCalendarWrapper>
              <CustomCalerdar />
            </CustomCalendarWrapper>
          </Container>
          <Container>next</Container>
          <Container>reviews</Container>
        </MenuSection>
      </BodyLayout>
      <Footer />
    </>
  );
};

export default MemberHome;
