import React, { useState } from "react";
import Form from "../components/body/mixin/Form";
import { BodyLayout } from "../components/body/mixin/Mixin";
import Footer from "../components/footer/Footer";
import Header from "../components/header/Header";

const IndividualForm = ({ isIndividual = true }) => {
  const [userId, setUserId] = useState("");
  const [pwd, setPwd] = useState("");
  // 비밀번호 체크용(userPwd2)
  const [isSame, setIsSame] = useState(false);
  const [name, setName] = useState("");
  const [num, setNum] = useState("");
  const [tel, setTel] = useState("");
  const [email, setEmail] = useState("");
  // 임시값
  const [zip, setZip] = useState("12345");
  const [addr, setAddr] = useState("");
  const [addrDetail, setAddrDetail] = useState("");
  const [startdate, setStartdate] = useState("");
  const [photo, setPhoto] = useState(null);
  // 미리보기용 url
  const [photoUrl, setPhotoUrl] = useState("");
  const [opt, setOpt] = useState([]);

  // 비밀번호 더블체크
  const checkPwd = (e) => {
    if (e.target.value === "") return;
    return pwd === e.target.value ? setIsSame(true) : setIsSame(false);
  };

  // 하이픈 자동 입력
  const insertHyphen = (e, num) => {
    // console.log(e.target.value.length, num);
    // console.log("키코드", e.keyCode);
    return e.target.value.length === num && e.keyCode !== 8 && e.keyCode !== 46
      ? (e.target.value += "-")
      : e.target.value;
  };

  // 업로드 이미지 미리보기
  const onChangePhoto = (e) => {
    const imageFile = e.target.files[0];
    const reader = new FileReader();
    reader.onload = () => {
      setPhoto(imageFile);
      setPhotoUrl(reader.result);
    };
    reader.readAsDataURL(imageFile);
  };

  // 체크박스 리스트 만들기
  const setOptList = (e) => {
    setOpt((prevState) => {
      console.log(prevState);
      return prevState.includes(e.target.value)
        ? prevState.filter((opt) => opt !== e.target.value)
        : [...prevState, e.target.value];
    });
  };

  const onSubmitForm = (e) => {
    e.preventDefault();
    const body = {
      userId: userId,
      pwd: pwd,
      name: name,
      num: num,
      tel: tel,
      email: email,
      zip: zip,
      addr: addr + " " + addrDetail,
      startdate: startdate,
      photoUrl: photoUrl,
      opt: opt.join("/"),
    };
    console.log("바디", body);

    axios
      .post("/wherewego/getUserData", body)
      .then((response) => {
        console.log("response : ", response.data);
        if (response.data > 0) {
          alert("회원가입이 완료되었습니다!");
          window.location.href = "http://localhost:3000/#/login";
        } else {
          alert("회원가입에 실패하였습니다...!!!");
        }
      })
      .catch((error) => {
        console.log("failed", error);
        alert("회원가입에 실패하였습니다...");
        // window.history.back();
      });
  };

  return (
    <>
      <Header />
      <BodyLayout>
        <Form
          id="individual_form"
          onSubmit={onSubmitForm}
          encType="multipart/form-data"
        >
          <ItemContainer>
            <FormInput
              type="text"
              name="userId"
              id="userId"
              required
              minLenght="6"
              maxLength="14"
              pattern="[A-Za-z]{1}\w{5,14}"
              placeholder="아이디"
              onKeyDown={(e) => setUserId(e.target.value)}
            />
            <Button type="button">중복 확인</Button>
            <ErrorMsg>
              아이디는 6~14자의 영문 대소문자, 숫자로 이루어져야 하며, 첫 글자는
              영문자만 입력할 수 있습니다.
            </ErrorMsg>
          </ItemContainer>
        </Form>
      </BodyLayout>
      <Footer />
    </>
  );
};

export default IndividualForm;
