import axios from "axios";
import React, { useEffect, useState } from "react";
import styled from "styled-components";
import { BodyLayout, MenuTitle } from "../components/body/mixin/Mixin";
import Footer from "../components/footer/Footer";
import Header from "../components/header/Header";

const Container = styled.div`
  width: 72%;
  height: 100vw;
  margin-top: var(--margin-default);
  background-color: var(--color-bg);
  display: flex;
  justify-content: center;
  border-radius: 12px;
  border: 1px solid var(--color-blur);
`;

const ContentsBox = styled.div`
  width: 800px;
  display: flex;
  flex-direction: column;
`;

const MyCourseItem = ({ match }) => {
  const [matchedData, setMatchedData] = useState(null);
  const [isLoaded, setIsLoaded] = useState(false);
  console.log("매치", match.params);
  const cnum = parseInt(match.params.cnum);
  console.log(cnum);

  useEffect(() => {
    axios
      .post("/wherewego/userCourseDetail", { c_num: cnum })
      .then((response) => {
        console.log(response.data);
        setMatchedData(response.data);
        setIsLoaded(true);
      })
      .catch((error) => console.log(error));
  }, []);
  return (
    <>
      <Header />
      <BodyLayout>
        <MenuTitle>{isLoaded ? matchedData?.name : null}</MenuTitle>
        <Container>
          <ContentsBox>ㅎㅎㅎ</ContentsBox>
        </Container>
      </BodyLayout>
      <Footer />
    </>
  );
};

export default MyCourseItem;
