import React, { useState } from "react";
import styled from "styled-components";
import CustomCalerdar from "../../calendar/CustomCalerdar";
import CheckBoxSet from "../../mixin/CheckBoxSet";
import Form from "../../mixin/Form";
import { Button } from "../../mixin/Mixin";
import { read_cookie } from "sfcookies";

const Container = styled.div`
  width: 100vw;
  display: flex;
  justify-content: center;
`;

const SearchForm = styled(Form)`
  & > div {
    padding: calc(var(--padding-default) * 2);
  }
`;

const Label = styled.label`
  font-size: var(--font-size-normal);
  margin-bottom: var(--margin-default);
`;

const ItemContainer = styled.div`
  width: 100%;
  display: flex;
  margin-bottom: var(--margin-default);
`;

const TitleSection = styled.section`
  width: 300px;
`;

const ContentSection = styled.section`
  width: calc(100% - 300px);
  display: flex;
  flex-direction: column;
  align-items: flex-end;
`;

const OptionContainer = styled(ItemContainer)`
  flex-direction: column;
`;

const DatePicker = styled.div`
  display: flex;
  flex-direction: column;
`;

const Caption = styled.section`
  width: 480px;
  display: flex;
  justify-content: space-between;
`;

const Indicator = styled.div`
  display: flex;
  padding-right: calc(var(--padding-default) * 2);
`;

const ColorIndicator = styled.div`
  width: 40px;
  height: 20px;
  margin-right: calc(var(--margin-default) / 4);
  background-color: ${(props) => props.bgColor || "inherit"};
`;

const TextIndicator = styled.div`
  font-size: var(--font-size-small);
  margin-right: calc(var(--margin-default) / 4);
`;

const SearchButton = styled(Button)`
  margin-top: var(--margin-default);
`;

const PlanningETC = ({
  selectedDate = null,
  setSelectedDate = () => {},
  selectedStation = "",
  lineNum = "",
}) => {
  const [time, setTime] = useState(0);
  const [sort, setSort] = useState("");
  const [opt, setOpt] = useState([]);
  // console.log("ETC 페이지", selectedDate);
  console.log("ETC", selectedDate);
  console.log("ETC", selectedStation);

  // 예약 날짜
  const stringifyResdate = () => {
    const dDay = new Date(selectedDate);
    const year = dDay.getFullYear();
    const month = dDay.getMonth() + 1;
    const date = dDay.getDate();
    return `${year}-${month}-${date}`;
  };

  const onSubmitForm = (e) => {
    e.preventDefault();
    const body = {
      userId: read_cookie("userId"),
      line: lineNum,
      stname: selectedStation,
      resdate: stringifyResdate(),
      // time:,
      // sort:,
      // opt:,
    };
    console.log(body);
  };
  return (
    <Container>
      <SearchForm>
        <ItemContainer>
          <TitleSection>
            <Label>데이트 날짜를 선택하세요.</Label>
          </TitleSection>
          <ContentSection>
            <Indicator>
              <ColorIndicator bgColor="var(--color-focus)" />
              <TextIndicator>오늘</TextIndicator>
              <ColorIndicator bgColor="var(--color-green)" />
              <TextIndicator>예정</TextIndicator>
            </Indicator>
            <CustomCalerdar
              selectedDate={selectedDate}
              setSelectedDate={setSelectedDate}
            />
          </ContentSection>
        </ItemContainer>
        <ItemContainer>
          <TitleSection>
            <Label>데이트 시작할 시간을 선택하세요.</Label>
          </TitleSection>
          <ContentSection>
            <Indicator>
              <select onChange={onChangeTime}>
                <option value="10~12">10:00 ~ 12:00</option>
                <option value="12~14">12:00 ~ 14:00</option>
                <option value="14~16">14:00 ~ 16:00</option>
                <option value="16~18">16:00 ~ 18:00</option>
                <option value="18~20">18:00 ~ 20:00</option>
              </select>
            </Indicator>
          </ContentSection>
        </ItemContainer>
        <ItemContainer>
          <TitleSection>
            <Label>데이트 순서를 선택하세요.</Label>
          </TitleSection>
          <ContentSection>
            <Indicator>
              <select name="first">
                <option name="none">장소1</option>
                <option name="pcode">식당</option>
                <option name="pcode2">카페</option>
                <option name="pcode3">기타</option>
              </select>
              <select name="second">
                <option name="none">장소2</option>
                <option name="pcode">식당</option>
                <option name="pcode2">카페</option>
                <option name="pcode3">기타</option>
              </select>
              <select name="third">
                <option name="none">장소3</option>
                <option name="pcode">식당</option>
                <option name="pcode2">카페</option>
                <option name="pcode3">기타</option>
              </select>
            </Indicator>
          </ContentSection>
        </ItemContainer>
        <OptionContainer>
          <Label>관심사를 선택하세요 </Label>
          <CheckBoxSet setOpt={setOpt} />
          {/* 앞에서 선택해둔 역 히든으로 가져가기 */}
          <input type="hidden" name="stname" value={selectedStation} />
        </OptionContainer>
        <SearchButton
          type="button"
          onClick={onSubmitForm}
          onSubmit={onSubmitForm}
        >
          코스 검색
        </SearchButton>
      </SearchForm>
    </Container>
  );
};

export default PlanningETC;
