import styled from "styled-components";

export const MenuTitle = styled.div`
  width: 60vw;
  margin-top: var(--margin-header-to-body);
  margin-bottom: var(--margin-default);
  font-size: var(--font-size-title-normal);
`;

// 버튼
export const Button = styled.button`
  margin: 0 var(--margin-line-space);
  padding: var(--padding-small);
  font-weight: ${(props) => props.weight || "800"};
  font-size: var(--font-size-small);
  background-color: var(--color-brown);
  border: none;
  border-radius: 4px;
  color: white;
  transition-duration: 0.2s;
  :hover {
    cursor: pointer;
    transform: scale(1.02);
    color: var(--color-yellow);
  }
`;

// input 디폴트
export const Input = styled.input`
  width: ${(props) => props.width || "12em"};
  padding: var(--padding-small);
  font-size: var(--font-size-normal);
  position: relative;
  text-align: center;
  border: none;
  border-bottom: 2px solid var(--color-black);
  transition-duration: 0.2s;
  &:focus {
    outline: none;
    border-bottom: 2px solid var(--color-yellow);
  }
`;

// 검색 폼
export const SearchBar = styled.div`
  width: ${(props) => props.width || "1200px"};
  height: 60px;
  margin-top: var(--margin-default);
  display: flex;
  justify-content: flex-end;
  align-items: center;
  Input {
    margin-right: calc(var(--margin-default) / 2);
  }
  Button {
    i {
      font-size: var(--font-size-large);
    }
  }
`;

// 노란색 글자
export const YellowD = styled.span`
  color: var(--color-yellow);
`;

// 옵션 컨테이너
export const OptionsContainer = styled.div`
  max-width: 44vw;
  height: auto;
  margin-top: var(--margin-default);
  border: 2px solid var(--color-brown);
  border-radius: 4px;
  display: flex;
  flex-wrap: wrap;
  padding: var(--padding-default);
`;

// 체크박스
export const Checkbox = styled.input`
  width: 18px;
  height: 18px;
  margin: 0 var(--margin-line-space);
`;

// 체크박스 라벨
export const CheckboxLabel = styled.label`
  display: flex;
  align-items: center;
  min-width: 4vw;
  margin: 1vh 1vw;
  :hover {
    cursor: pointer;
  }
`;
