import styled from "styled-components";

// 버튼
export const Button = styled.button`
  margin: 0 var(--margin-line-space);
  padding: var(--padding-small);
  font-weight: 800;
  background-color: var(--color-brown);
  border: none;
  border-radius: 4px;
  color: white;
  transition-duration: 0.2s;
  :hover {
    cursor: pointer;
    transform: scale(1.02);
    color: var(--color-yellow);
  }
`;

// 제출 버튼
export const SubmitButton = styled(Button)`
  width: 200px;
  margin: var(--margin-default) var(--margin-default) 0 0;
`;

// 인풋 라벨
export const Label = styled.label`
  width: 8vw;
  font-size: var(--font-size-normal);
  margin-right: var(--margin-default);
`;

// 인풋 상자(입력칸 디폴트)
export const Input = styled.input`
  width: ${(props) => props.width || "12em"};
  padding: var(--padding-small);
  font-size: var(--font-size-normal);
  position: relative;
  text-align: center;
  border: none;
  border-bottom: 2px solid var(--color-black);
  transition-duration: 0.2s;
  &:focus {
    outline: none;
    border-bottom: 2px solid var(--color-yellow);
  }
  &:valid {
    background-image: url("/images/validcheck.png");
    background-size: 16px;
    background-repeat: no-repeat;
    background-position: 12px 12px;
  }
  &:not(:focus):invalid {
    border-bottom: 2px solid var(--color-pink);
  }
`;

// 하이픈
export const Hyphen = styled.p`
  margin: 0 calc(var(--margin-default) / 4);
  font-size: var(--font-size-large);
  color: var(--color-black);
`;

// 체크박스
export const Checkbox = styled.input`
  width: 18px;
  height: 18px;
  margin: 0 var(--margin-line-space);
`;

// 체크박스 라벨
export const CheckboxLabel = styled.label`
  display: flex;
  align-items: center;
  min-width: 4vw;
  margin: 1vh 1vw;
  :hover {
    cursor: pointer;
  }
`;

// 각 항목의 컨테이너
export const ItemContainer = styled.div`
  width: auto;
  margin-top: calc(var(--margin-default) / 2);
  display: flex;
  justify-content: flex-start;
  align-items: center;

  /* Null시 출력될 주의사항 => :invalid로 대체 */
  ::after {
    margin-left: calc(var(--margin-default) / 4);
    font-size: var(--font-size-tiny);
    color: var(--color-pink);
    display: none;
  }
`;

// 프롭스 전달은 잘 됨...
export const IDContainer = styled(ItemContainer)`
  ::after {
    display: ${(props) => {
      // console.log(props);
      // console.log("프롭스 어디가쒀", props.formError);
      return props.formError === "" ? "none" : "block";
    }};
    content: "아이디는 최대 12자까지 허용됩니다!";
    /* content: ${(props) => {
      return props.formError === "idLengthError"
        ? "아이디는 최대 12자까지 허용됩니다!"
        : props.formError === "checkError"
        ? "아이디 중복 여부를 확인해 주세요!"
        : props.formError === "nullError"
        ? "아이디를 입력해 주세요"
        : null;
    }}; */
    margin-left: 0;
  }
`;
export const PWDContainer = styled(ItemContainer)`
  ::after {
    content: "비밀번호를 입력해 주세요!";
  }
`;
export const PWDCheckContainer = styled(ItemContainer)`
  ::after {
    content: "비밀번호 일치 여부를 확인해 주세요!";
  }
`;
export const NameContainer = styled(ItemContainer)`
  ::after {
    content: "이름을 입력해 주세요!";
  }
`;
export const PersonalNumContainer = styled(ItemContainer)`
  ::after {
    content: "유효한 주민등록번호를 입력해 주세요!";
  }
`;
export const BusinessNumContainer = styled(ItemContainer)`
  ::after {
    content: "유효한 사업자등록번호를 입력해 주세요!";
  }
`;
export const PhoneNumContainer = styled(ItemContainer)`
  ::after {
    content: "연락처를 입력해 주세요!";
  }
`;
