import React, { useState } from "react";
import styled from "styled-components";

const Container = styled.div`
  display: flex;
  justify-content: center;
  transition-duration: 0.7s;
  transition-timing-function: cubic-bezier(0.57, 0.01, 0.36, 0.99);
  transform: translateX(
    ${(props) => {
      return props.idx === "first"
        ? "0"
        : props.idx === "second"
        ? "-50%"
        : null;
    }}
  );
`;

const PageSlider = ({ children, ...props }) => {
  const [idx, setIdx] = useState("first");
  console.log("슬라이더", props.idx);
  const changeIdx = () => {
    idx === "first" ? setIdx("second") : setIdx("first");
  };

  return (
    <Container onClick={changeIdx} idx={idx}>
      {children}
    </Container>
  );
};

export default PageSlider;
