import React, { useState } from "react";
import styled from "styled-components";
import axios from "axios";
import Form from "./Form";
import {
  Button,
  Label,
  Input,
  Hyphen,
  Checkbox,
  CheckboxLabel,
  SubmitButton,
  ItemContainer,
  IDContainer,
  PWDContainer,
  PWDCheckContainer,
  NameContainer,
  PersonalNumContainer,
  PhoneNumContainer,
  PhoneNumInput,
  EmailInput,
  ZipcodeInput,
  AddrInput,
  AddrDetailInput,
} from "../registrationForm/FormMixin";

const Container = styled.div`
  overflow: hidden;
  transition-delay: ${(props) => {
    return props.isVisible ? "0.5s" : "0";
  }};
  // auto에는 트랜지션 속도가 적용되지 않는 듯
  transition-duration: 0.5s;
  height: ${(props) => {
    return props.isVisible ? "120vh" : "0";
  }};
`;

const TopSection = styled.section`
  width: 100%;
  display: flex;
`;

const LeftSection = styled.section`
  width: 72%;
`;
const RightSection = styled.section`
  width: 28%;
  padding: var(--padding-default);
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
  align-items: center;
`;

const BottomSection = styled.section`
  width: 100%;
`;

const PersonalNumInput = styled(Input)`
  width: 4.5em;
`;

const AnniversaryInput = styled(Input)`
  width: 10em;
`;

// 옵션 컨테이너
const OptionsContainer = styled.div`
  max-width: 40vw;
  height: auto;
  margin-top: var(--margin-default);
  border: 2px solid var(--color-brown);
  border-radius: 4px;
  display: flex;
  flex-wrap: wrap;
  padding: var(--padding-default);
`;

// 사진 입력 컨테이너
const UserImgContainer = styled.div`
  width: 240px;
  height: 240px;
  border-radius: 50%;
  margin-bottom: var(--margin-line-space);
  display: flex;
  justify-content: center;
  align-items: center;
  font-size: var(--font-size-huge);
  background-color: var(--color-lightpink);
`;

// 제출 박스 컨테이너
const SubmitSection = styled.section`
  width: 100%;
  display: flex;
  justify-content: flex-end;
`;

const IndividualForm = ({ isIndividual = true }) => {
  const [userId, setUserId] = useState("");
  const [userPwd, setUserPwd] = useState("");
  const [userPwd2, setUserPwd2] = useState("");

  const onSubmitForm = (e) => {
    e.preventDefault();
    const body = {
      userId: userId,
      // userPwd: userPwd,
      // userPwd2: userPwd2,
    };

    // 이미지 전송용
    // const formData = new FormData();
    // formData.append("individualData", data[0]);

    axios
      .post(
        "/wherewego/user/getUserData",
        body
        // , { headers: { "Content-Type": `multipart/form-data` } },
      )
      .then((response) => {
        console.log("response : ", JSON.stringify(response, null, 2));
        window.location.href = "http://localhost:3000/";
      })
      .catch((error) => {
        console.log("failed", error);
      });
  };

  const onChangeUserId = (e) => {
    console.log(e.target.maxLength);
    return userId.length >= e.target.maxLength
      ? setUserId(userId.slice(0, e.target.maxLength))
      : setUserId(e.target.value);
  };

  return (
    <Container isVisible={isIndividual}>
      <Form
        id="individual_form"
        onSubmit={onSubmitForm}
        encType="multipart/form-data"
      >
        <TopSection>
          <LeftSection>
            <IDContainer>
              <Label htmlFor="userId">아이디</Label>
              <Input
                type="text"
                name="userId"
                id="userId"
                value={userId}
                onChange={onChangeUserId}
                required
                minLenght="6"
                maxLength="20"
                pattern="[A-Za-z]{1}\w{5,19}"
              />
              <Button>중복 확인</Button>
            </IDContainer>
            <PWDContainer>
              <Label>비밀번호</Label>
              <Input type="password" name="userPwd" id="userPwd" />
            </PWDContainer>
            <PWDCheckContainer>
              <Label>비밀번호 확인</Label>
              <Input type="password" name="userPwd2" id="userPwd2" />
            </PWDCheckContainer>
            <NameContainer>
              <Label>이름</Label>
              <Input type="text" name="userName" id="userName" />
            </NameContainer>
            <PersonalNumContainer>
              <Label>주민등록번호</Label>
              <PersonalNumInput type="text" name="userNum1" id="userNum1" />
              <Hyphen> - </Hyphen>
              <PersonalNumInput type="password" name="userNum2" id="userNum2" />
            </PersonalNumContainer>
            <PhoneNumContainer>
              <Label>연락처</Label>
              <PhoneNumInput type="select" name="user_tel1" id="user_tel1" />
              <Hyphen> - </Hyphen>
              <PhoneNumInput type="text" name="user_tel2" id="user_tel2" />
              <Hyphen> - </Hyphen>
              <PhoneNumInput type="text" name="user_tel3" id="user_tel3" />
            </PhoneNumContainer>
            <ItemContainer>
              <Label>이메일</Label>
              <EmailInput tyoe="email"></EmailInput>
            </ItemContainer>
            <ItemContainer>
              <Label>우편번호</Label>
              <ZipcodeInput type="text" name="user_zipcode" id="user_zipcode" />
              <Button>우편번호 검색</Button>
            </ItemContainer>
            <ItemContainer>
              <Label>주소</Label>
              <AddrInput type="text" name="user_addr" id="user_addr" />
            </ItemContainer>
            <ItemContainer>
              <Label>상세주소</Label>
              <AddrDetailInput
                type="text"
                name="user_addrdetail"
                id="user_addrdetail"
              />
            </ItemContainer>
            <ItemContainer>
              <Label>기념일</Label>
              <AnniversaryInput type="date" name="user_date" id="user_date" />
            </ItemContainer>
          </LeftSection>
          <RightSection>
            <UserImgContainer>
              <i className="far fa-user"></i>
            </UserImgContainer>
            <Button>사진 선택</Button>
          </RightSection>
        </TopSection>

        {/* 바텀 폼 */}
        <BottomSection>
          <ItemContainer>
            <Label>관심사</Label>
            <OptionsContainer>
              <CheckboxLabel>
                <Checkbox type="checkbox" name="user_option" id="" /> 임시
              </CheckboxLabel>
              <CheckboxLabel>
                <Checkbox type="checkbox" name="user_option" /> 로
              </CheckboxLabel>
              <CheckboxLabel>
                <Checkbox type="checkbox" name="user_option" /> 다양한 값을
                입력하여
              </CheckboxLabel>
              <CheckboxLabel>
                <Checkbox type="checkbox" name="user_option" /> 박스의 크기가
                예쁘게 늘어나는지 확인하기 위한
              </CheckboxLabel>
              <CheckboxLabel>
                <Checkbox type="checkbox" name="user_option" /> 체크박스입니다.
              </CheckboxLabel>
              <CheckboxLabel>
                <Checkbox type="checkbox" name="user_option" /> 값은 아직
              </CheckboxLabel>
              <CheckboxLabel>
                <Checkbox type="checkbox" name="user_option" /> 전달되지
                않는다는 사실을 부디 명심하시고
              </CheckboxLabel>
              <CheckboxLabel>
                <Checkbox type="checkbox" name="user_option" /> 값이 전달되지
                않는다고 엄한 노트북에 샷건을 치지 않기를
              </CheckboxLabel>
              <CheckboxLabel>
                <Checkbox type="checkbox" name="user_option" /> 간절하게
                바랍니다.
              </CheckboxLabel>
              <CheckboxLabel>
                <Checkbox type="checkbox" name="user_option" /> 이만
              </CheckboxLabel>
              <CheckboxLabel>
                <Checkbox type="checkbox" name="user_option" /> 아직도 있나?
              </CheckboxLabel>
              <CheckboxLabel>
                <Checkbox type="checkbox" name="user_option" /> 그만하자
              </CheckboxLabel>
              <CheckboxLabel>
                <Checkbox type="checkbox" name="user_option" /> 이정도면 됐지
              </CheckboxLabel>
            </OptionsContainer>
          </ItemContainer>
        </BottomSection>
        <SubmitSection>
          <SubmitButton as="input" type="reset" value="초기화" />
          <SubmitButton as="input" type="submit" value="회원가입" />
        </SubmitSection>
      </Form>
    </Container>
  );
};

export default IndividualForm;
