import React, { memo, useEffect, useRef, useState } from "react";
import {
  DirectionsRenderer,
  DirectionsService,
  GoogleMap,
  LoadScript,
} from "@react-google-maps/api";

// props는 상위 컴포넌트에서 전달받은 내용이 될 것
const CustomGoogleDirections = (props) => {
  const [directions, setDirections] = useState();
  const { origin, stopover, destination } = props;
  const count = useRef(0);

  const options = {
    polyLineOptions: {
      strokeColor: "black",
      strokeWeight: 6,
      strokeOpacity: 0.8,
    },
  };

  useEffect(() => {
    count.current = 0;
    console.log(origin, stopover, destination);
  }, [
    origin.lat,
    origin.lng,
    stopover.lat,
    stopover.lng,
    destination.lat,
    destination.lng,
  ]);

  const directionsCallback = (result, status) => {
    console.log(result, status);
    if (status === "OK" && count.current === 0) {
      count.current += 1;
      setDirections(result);
    }
  };

  return (
    <>
      <DirectionsService
        // travelMode: 'TRANSIT' or 'WALKING'
        options={{ origin, destination, travelMode: "TRANSIT" }}
        callback={directionsCallback}
      />
      <DirectionsRenderer directions={directions} options={options} />
    </>
  );
};

const CustomGoogleMap = (props) => {
  const { startPoint, endPoint } = props;
  return (
    <LoadScript googleMapsApiKey={process.env.REACT_APP_MY_KEY}>
      <GoogleMap
        mapContainerStyle={{
          height: "400px",
          width: "800px",
        }}
        zoom={15}
        center={startPoint ? { lat: 37.579779, lng: 126.976955 } : undefined}
      >
        <CustomGoogleDirections origin={startPoint} destination={endPoint} />
      </GoogleMap>
    </LoadScript>
  );
};

export default memo(CustomGoogleMap);
