import { useState } from "react";

const UseToggle = () => {
  const [fullHeight, setFullHeight] = useState("0");
  const [arrowRotation, setArrowRotation] = useState("0");

  const setOpen = () => {
    setFullHeight("60px");
    setArrowRotation("0.5turn");
  };
  const setClose = () => {
    setFullHeight("0");
    setArrowRotation("0");
  };
  const toggleState = () => {
    fullHeight === "60px" ? setClose() : setOpen();
  };
  return { setOpen, setClose, toggleState };
};

export default UseToggle;
