// ★★★ 미구현 기능
import React, { useState, useEffect, useRef } from "react";
import styled from "styled-components";
import { Link } from "react-router-dom";
import { read_cookie, delete_cookie } from "sfcookies";
import { YellowD } from "../body/mixin/Mixin";
import BorderEffect from "../body/mixin/BorderEffect";

// 인터섹션 옵저버....시도
// const Observer = styled.div`
//   width: 100%;
//   height: auto;
//   position: relative;
//   top: 0;
// `;

// const Criteria = styled.div`
//   width: 100%;
//   height: 60px;
//   position: absolute;
//   top: 0;
//   background-color: red;
// `;

const HeaderContainer = styled.header`
  width: calc(100% - 16px);
  height: var(--header-height);
  display: flex;
  justify-content: center;
  /* background-color: var(--color-brown); */
  background-color: ${(props) => props.bgColor};
  position: fixed;
  z-index: 2;
  opacity: 0.9;
  color: white;
`;

const HeaderSizeController = styled.div`
  width: 64%;
  display: flex;
  justify-content: space-between;
`;

const LogoContainer = styled.div`
  position: relative;
  font-size: var(--font-size-title-large);
  a {
    height: 100%;
    display: flex;
    justify-content: flex-start;
    align-items: center;
  }
`;

const MenuSection = styled.section`
  width: 64%;
  display: flex;
  justify-content: flex-end;
`;

const MenuContainer = styled.ul`
  width: 80%;
  min-width: 460px;
  height: 100%;
  display: flex;
  justify-content: flex-start;
  align-items: center;
  font-size: var(--font-size-large);
  & > li {
    margin-right: var(--margin-default);
  }
  & > li:hover {
    color: var(--color-yellow);
  }
`;

const LoginButtonContainer = styled.div`
  width: 20%;
  min-width: 152px;
  height: 100%;
  display: flex;
  align-items: center;
  justify-content: space-evenly;
  font-size: var(--font-size-small);
  color: grey;
  & > div {
    position: relative;
  }
  & > div:hover {
    color: var(--color-yellow);
  }
`;

const SubMenuContainer = styled.div`
  height: 100%;
  display: flex;
  align-items: center;
`;

const SubMenu = styled.ul`
  min-width: 152px;
  height: ${(props) => props.heightOfSub};
  transition-duration: 0.3s;
  overflow: hidden;
  background-color: var(--color-brown);
  position: absolute;
  top: 60px;
  left: -48px;
`;

const SubMenuItem = styled.li`
  height: 60px;
  display: flex;
  justify-content: center;
  align-items: center;
  color: white;
  position: relative;
  &:hover {
    cursor: pointer;
    color: var(--color-yellow);
  }
`;

const Header = () => {
  const [heightOfSub, setHeightOfSub] = useState("0");
  const [homeWidth, setHomeWidth] = useState("0");
  const [profileWidth, setProfileWidth] = useState("0");
  const [recommendWidth, setRecommendWidth] = useState("0");
  const [reviewWidth, setReviewWidth] = useState("0");
  const [likeWidth, setLikeWidth] = useState("0");
  const [pointWidth, setPointWidth] = useState("0");
  // 헤더 색 변화시키기
  const [bgColor, setBgColor] = useState("var(--color-brown)");
  const headerRef = useRef(null);
  // 로그아웃
  const onClickLogout = () => {
    delete_cookie("userId");
  };

  // useEffect(() => {
  //   // intersection observer로 헤더의 변화에 따라 색깔 변화시키기
  //   console.log("옵저버 생성?");
  //   const options = {
  //     // 브라우저 기준(null)
  //     root: null,
  //     rootMargin: "0px",
  //     // 1: 모든 픽셀이 화면에 노출되면 콜백
  //     // 0: 요소가 1픽셀이라도 보이면 콜백
  //     thresold: 0.75,
  //   };
  //   console.log(options);

  //   const callback = () => {
  //     setBgColor("black");
  //   };

  //   const observer = new IntersectionObserver(callback, options);
  //   console.log(observer);

  //   console.log(headerRef.current);
  //   observer.observe(headerRef.current);
  //   console.log("디드 마운트", bgColor);
  //   return () => observer.disconnect();
  // }, []);

  // console.log("첫 마운트", bgColor);

  return (
    // <Observer>
    //   <Criteria ref={headerRef} id="criteria" />
    <HeaderContainer bgColor={bgColor}>
      <HeaderSizeController>
        <LogoContainer>
          <Link to={`/`}>
            Plan.<YellowD>D</YellowD>
          </Link>
        </LogoContainer>
        <MenuSection>
          {read_cookie("userId").length > 0 ? (
            <MenuContainer>
              <li>
                <Link to={`/memberhome`}>Home</Link>
              </li>
              <li>
                <Link to={`/planning`}>Course</Link>
              </li>
              <li>
                <Link to={`/searchplace`}>Place</Link>
              </li>
              <li>
                <Link to={`/reviews2`}>Reviews</Link>
              </li>
              <li>
                <Link to={`/userrecommendation`}>Recommendation</Link>
              </li>
            </MenuContainer>
          ) : null}
          <LoginButtonContainer>
            <SubMenuContainer
              onMouseOver={() => setHeightOfSub("360px")}
              onMouseOut={() => setHeightOfSub("0")}
            >
              {read_cookie("userId").length > 0 ? (
                <>
                  <div>Mypage</div>
                  <SubMenu heightOfSub={heightOfSub}>
                    <Link to={"/mypage"}>
                      <SubMenuItem
                        onMouseOver={() => setHomeWidth("150px")}
                        onMouseOut={() => setHomeWidth("0")}
                      >
                        MyHome
                        <BorderEffect spanWidth={homeWidth} />
                      </SubMenuItem>
                    </Link>
                    <Link to={"/editprofile"}>
                      <SubMenuItem
                        onMouseOver={() => setProfileWidth("150px")}
                        onMouseOut={() => setProfileWidth("0")}
                      >
                        Profile
                        <BorderEffect spanWidth={profileWidth} />
                      </SubMenuItem>
                    </Link>
                    <Link to={"/myhistory"}>
                      <SubMenuItem
                        onMouseOver={() => setReviewWidth("150px")}
                        onMouseOut={() => setReviewWidth("0")}
                      >
                        History
                        <BorderEffect spanWidth={reviewWidth} />
                      </SubMenuItem>
                    </Link>
                    <Link to={"/myrecommendation"}>
                      <SubMenuItem
                        onMouseOver={() => setRecommendWidth("150px")}
                        onMouseOut={() => setRecommendWidth("0")}
                      >
                        MyRecommendation
                        <BorderEffect spanWidth={recommendWidth} />
                      </SubMenuItem>
                    </Link>
                    <Link to={"/mydibs"}>
                      <SubMenuItem
                        onMouseOver={() => setLikeWidth("150px")}
                        onMouseOut={() => setLikeWidth("0")}
                      >
                        Dibs
                        <BorderEffect spanWidth={likeWidth} />
                      </SubMenuItem>
                    </Link>
                    <Link to={"/pointshop"}>
                      <SubMenuItem
                        onMouseOver={() => setPointWidth("150px")}
                        onMouseOut={() => setPointWidth("0")}
                      >
                        PointShop
                        <BorderEffect spanWidth={pointWidth} />
                      </SubMenuItem>
                    </Link>
                  </SubMenu>
                </>
              ) : (
                <Link to={`/registration`}>Sign up</Link>
              )}
            </SubMenuContainer>
            <div>
              {read_cookie("userId").length > 0 ? (
                <Link to={`/`} onClick={onClickLogout}>
                  LogOut
                </Link>
              ) : (
                <Link to={`/login`}>Sign in</Link>
              )}
            </div>
          </LoginButtonContainer>
        </MenuSection>
      </HeaderSizeController>
    </HeaderContainer>
    // </Observer>
  );
};

export default Header;
